ingredientsArray.push( [ 'CostanzoRecipes_Sides_LemonGarlicKaleSalad', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="2 cups" fraction="2.000000000000" units="cups">2 cups</amount> sliced almonds (or other nuts)</li><li onclick="handleItemClick(this)"> <amount original_amount="⅓ cup" fraction="0.333333333333" units="cup">⅓ cup</amount> freshly squeezed lemon juice (from <amount original_amount="2 lemons" fraction="2.000000000000" units="">2</amount>to <amount original_amount="4 lemons" fraction="4.000000000000" units="lemons">4 lemons</amount>)</li><li onclick="handleItemClick(this)"> Kosher salt </li><li onclick="handleItemClick(this)"> <amount original_amount="1½ cups" fraction="1.500000000000" units="cups">1½ cups</amount> extra-virgin olive oil</li><li onclick="handleItemClick(this)"> <amount original_amount="4 cloves" fraction="4.000000000000" units="cloves">4 cloves</amount> garlic, crushed with the flat side of a knife, peeled and left whole</li><li onclick="handleItemClick(this)"> <amount original_amount="10 ounces" fraction="10.000000000000" units="">10</amount>to <amount original_amount="12 ounces" fraction="12.000000000000" units="ounces">12 ounces</amount> washed and dried kale leaves, thick stems removed (weight after trimming)</li><li onclick="handleItemClick(this)"> <amount original_amount="1½ cups" fraction="1.500000000000" units="cups">1½ cups</amount> freshly grated Parmesan (<i>optional</i>) </li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Sides_LemonGarlicKaleSalad', '<p><ol><li onclick="handleItemClick(this)"> In a toaster oven or skillet, toast almonds until golden brown and fragrant. Set aside to cool. </li><li onclick="handleItemClick(this)"> In a bowl, combine lemon juice and 1 heaping teaspoon salt. Slowly whisk in olive oil. Add garlic cloves and set aside to steep. </li><li onclick="handleItemClick(this)"> Working in batches, cut the kale into thin ribbons: gather <amount original_amount="a large" fraction="1.000000000000" units="large">a large</amount> handful of leaves, bunch together tightly, and use the other hand to slice into 1/4-inch-thick pieces.<div class="note">    This need not be done very precisely or neatly; the idea is to end up with a kind of slaw. </div><div class="note">    Recipe can be made up to this point 1 day ahead. Keep kale and dressing refrigerated separately. </div></li><li onclick="handleItemClick(this)"> Place chopped kale in a very large bowl. Sprinkle surface with almonds and then with cheese, if using. Remove and discard garlic cloves from dressing. Pour half the dressing over the salad and toss. Taste for dressing and salt and add more as needed, tossing to coat thoroughly. Serve within 1 hour. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 3600)</script></div></li></ol>' ] );
