ingredientsArray.push( [ 'CostanzoRecipes_Entrees_SweetPotatoBlackBeanBurgers', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 medium" fraction="1.000000000000" units="medium">1 medium</amount> sweet potato</li><li onclick="handleItemClick(this)"> <amount original_amount="1 15-ounce can" fraction="1.000000000000" units="15-ounce can" implied_unintary="true">1 15-ounce can</amount> black beans, drained and rinsed</li><li onclick="handleItemClick(this)"> <amount original_amount="¼ cup" fraction="0.250000000000" units="cup">¼ cup</amount> chopped onion</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> rolled oats</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> ground cumin</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> paprika</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> onion powder</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> garlic powder</li></ul><h3 id="header-section-section-1" onclick="toggleSection(\'section-1\')">Sriracha Mayo (optional)<span id="heading-ellipses-section-1"></h3><div id=\'heading-content-section-1\'><p><ul><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> vegan mayo</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> sriracha hot sauce</li><li onclick="handleItemClick(this)"> pinch garlic powder </li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Entrees_SweetPotatoBlackBeanBurgers', '<p><ol><li onclick="handleItemClick(this)"> Preheat oven to 400°. </li><li onclick="handleItemClick(this)"> Cut potato in half lengthwise and place on pan. </li><li onclick="handleItemClick(this)"> Bake for 30 minutes until fork tender; remove from oven. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 1800)</script></div></li><li onclick="handleItemClick(this)"> Lower oven to 375°. </li><li onclick="handleItemClick(this)"> When potato is cool enough to handle, peel off skin. </li><li onclick="handleItemClick(this)"> Add flesh to large bowl and mash. </li><li onclick="handleItemClick(this)"> Add black beans and mash lightly into potato, leaving some beans whole. </li><li onclick="handleItemClick(this)"> Stir in onion, oats, cumin, paprika, onion powder and garlic powder. </li><li onclick="handleItemClick(this)"> Form burgers. </li><li onclick="handleItemClick(this)"> Lightly oil a pan. </li><li onclick="handleItemClick(this)"> Fry 15 minutes on each side. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-3\')" class="alarm-clock-icon"><div id="CircularTimer-3" class="circular-timer-container"><script>new CircularTimer("CircularTimer-3", 900)</script></div></li><li onclick="handleItemClick(this)"> Sever on rolls or over salad. </li></ol>' ] );
