creditsArray.push( [ 'CostanzoRecipes_Entrees_PastaMilanese', '<p><ul><li onclick="handleItemClick(this)"> From <a href="https://www.wsj.com/articles/pasta-milanese-11615472510?mod=searchresults_pos1&page=1" target="_blank">https://www.wsj.com/articles/pasta-milanese-11615472510?mod=searchresults_pos1&page=1</a>  </li><li onclick="handleItemClick(this)"> date created: [2021-03-19 Fri]   </li><li onclick="handleItemClick(this)">[2021-03-18 Thu]  Made for T; we\'d definitely get this in a restaurant. </li><li onclick="handleItemClick(this)">[2024-03-10 Sun]  Made for T, Greg, Mary Ann and the Grosecloses. Good once again, but a bit dry. Perhaps the sauce cooked too long (even though I added some pasta water toward the end). </li></ul>' ] );
