methodsArray.push( [ 'CostanzoRecipes_Entrees_MacaroniandCheese', '<p><ol><li onclick="handleItemClick(this)"> Cook macaroni. </li><li onclick="handleItemClick(this)"> Drain. </li><li onclick="handleItemClick(this)"> Preheat oven to 350° F. </li><li onclick="handleItemClick(this)"> Grease <amount original_amount="2 quart" fraction="2.000000000000" units="quart">2 quart</amount> casserole.</li><li onclick="handleItemClick(this)"> In a small saucepan over medium heat, melt <amount original_amount="2 Tablespoons" fraction="2.000000000000" units="Tablespoons">2 Tablespoons</amount> butter, add bread crumbs and toss to coat. Set aside.</li><li onclick="handleItemClick(this)"> In a medium saucepan over medium heat, melt remaining <amount original_amount="2 Tablespoons" fraction="2.000000000000" units="Tablespoons">2 Tablespoons</amount> butter, add onion and cook until tender, about 5 minutes.<img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 300)</script></div></li><li onclick="handleItemClick(this)"> Stir in flour, salt, mustard and pepper. </li><li onclick="handleItemClick(this)"> Slowly stir in milk and cook until smooth and slightly thickened. </li><li onclick="handleItemClick(this)"> Stir in cheese until melted. </li><li onclick="handleItemClick(this)"> Place macaroni in casserole and pour cheese mixture over macaroni. </li><li onclick="handleItemClick(this)"> Sprinkle with reserved bread crumbs. </li><li onclick="handleItemClick(this)"> Bake 20 minutes. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 1200)</script></div></li></ol>' ] );
