ingredientsArray.push( [ 'CostanzoRecipes_Desserts_VeganPeanutButterCookies', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> natural peanut butter (<amount original_amount="270 g" fraction="270.000000000000" units="g">270 g</amount>), creamy or crunchy (or almond butter)</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> coconut sugar or pure cane sugar (<amount original_amount="192 g" fraction="192.000000000000" units="g">192 g</amount>)</li><li onclick="handleItemClick(this)"> <amount original_amount="6 tablespoons" fraction="6.000000000000" units="tablespoons">6 tablespoons</amount> almond milk, room temp</li><li onclick="handleItemClick(this)"> <amount original_amount="2 teaspoons" fraction="2.000000000000" units="teaspoons">2 teaspoons</amount> vanilla extract</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> flour (<amount original_amount="120 g" fraction="120.000000000000" units="g">120 g</amount>), spelt, unbleached all-purpose, almond flour, or 1-1 gluten-free blend</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> baking soda</li><li onclick="handleItemClick(this)"> generous pinch of mineral salt (only use if nut butter is unsalted) <div class="note">   Reduce the sugar to <amount original_amount="¾ cup" fraction="0.750000000000" units="cup">¾ cup</amount> for a less sweet cookie. It’s just as delicious!</div></li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Desserts_VeganPeanutButterCookies', '<p><ol><li onclick="handleItemClick(this)"> Preheat the oven to 350°F. </li><li onclick="handleItemClick(this)"> Line a cookie sheet with silicone mat, parchment paper or leave ungreased. </li><li onclick="handleItemClick(this)"> In a large bowl, add the peanut butter and sugar and mix until creamy.</li><li onclick="handleItemClick(this)"> Stir in the almond milk and vanilla. </li><li onclick="handleItemClick(this)"> Next, add the flour, baking soda, and salt, mix to combine. The dough will be tough to stir, using your hands to mix it together will be easier. </li><li onclick="handleItemClick(this)"> Using a <amount original_amount="1½ tablespoon" fraction="1.500000000000" units="tablespoon">1½ tablespoon</amount> scooper, scoop out the dough and roll into balls, about 1¼ inch.</li><li onclick="handleItemClick(this)"> Place the balls of dough about 2½ inches apart on a baking sheet. </li><li onclick="handleItemClick(this)"> Using the back of a fork, flatten in a crisscross pattern. </li><li onclick="handleItemClick(this)"> Optionally, sprinkle with sugar or coarse salt. </li><li onclick="handleItemClick(this)"> Place in the oven and bake for 10 – 13 minutes (13 minutes will yield a crispier cookie that’s tender inside). <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 600)</script><script>new CircularTimer("CircularTimer-1", 780)</script><script>new CircularTimer("CircularTimer-1", 780)</script></div></li><li onclick="handleItemClick(this)"> Let cool a few minutes, transfer cookies to wire rack and repeat with remaining dough. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 120)</script><script>new CircularTimer("CircularTimer-2", 300)</script></div></li></ol>' ] );
