ingredientsArray.push( [ 'CostanzoRecipes_Desserts_GingerSnaps', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="2 cups" fraction="2.000000000000" units="cups">2 cups</amount> all-purpose flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> ground ginger</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> ground cinnamon</li><li onclick="handleItemClick(this)"> <amount original_amount="2 teaspoons" fraction="2.000000000000" units="teaspoons">2 teaspoons</amount> baking soda</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="¾ cup" fraction="0.750000000000" units="cup">¾ cup</amount> vegetable shortening</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> granulated sugar</li><li onclick="handleItemClick(this)"> <amount original_amount="¼ cup" fraction="0.250000000000" units="cup">¼ cup</amount> light molasses</li><li onclick="handleItemClick(this)"> <amount original_amount="1 large" fraction="1.000000000000" units="large">1 large</amount> egg</li><li onclick="handleItemClick(this)"> Sugar for rolling </li></ul>' ] );
