ingredientsArray.push( [ 'CostanzoRecipes_Desserts_FreshCranAppleCake', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> light brown sugar, firmly packed</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> vegetable oil</li><li onclick="handleItemClick(this)"> <amount original_amount="2 cups" fraction="2.000000000000" units="cups">2 cups</amount> flour (may use whole wheat)</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> baking soda</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> nutmeg</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> cinnamon</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="2 eggs" fraction="2.000000000000" units="eggs">2 eggs</amount></li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> vanilla</li><li onclick="handleItemClick(this)"> <amount original_amount="2 cups" fraction="2.000000000000" units="cups">2 cups</amount> apples, sliced</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> chopped walnuts or <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> chopped pecans</li><li onclick="handleItemClick(this)"> <amount original_amount="½ lb" fraction="0.500000000000" units="lb">½ lb</amount> fresh cranberries</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Desserts_FreshCranAppleCake', '<p><ol><li onclick="handleItemClick(this)"> Cream together oil and sugar. </li><li onclick="handleItemClick(this)"> Add eggs and vanilla, and beat well. </li><li onclick="handleItemClick(this)"> Sift together flour and dry ingredients. </li><li onclick="handleItemClick(this)"> Add to first mixture and stir until thoroughly combined. </li><li onclick="handleItemClick(this)"> Stir in apples, cranberries and nuts. </li><li onclick="handleItemClick(this)"> This is a very thick mixture. </li><li onclick="handleItemClick(this)"> Heat oven to 350°F and bake in well greased 9x13-inch pan for 45-50 minutes. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 2700)</script><script>new CircularTimer("CircularTimer-1", 3000)</script></div></li></ol>' ] );
