ingredientsArray.push( [ 'CostanzoRecipes_Desserts_BlueberryCobbler', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="⅔ cup" fraction="0.666666666666" units="cup">⅔ cup</amount> all-purpose flour</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> sugar</li><li onclick="handleItemClick(this)"> <amount original_amount="1½ teasp" fraction="1.500000000000" units="teasp">1½ teasp</amount> baking powder</li><li onclick="handleItemClick(this)"> <amount original_amount="¼ teasp" fraction="0.250000000000" units="teasp">¼ teasp</amount> salt, if desired</li><li onclick="handleItemClick(this)"> <amount original_amount="⅔ cup" fraction="0.666666666666" units="cup">⅔ cup</amount> skim milk</li><li onclick="handleItemClick(this)"> 2 Tbls butter or margarine, melted </li><li onclick="handleItemClick(this)"> <amount original_amount="2 cups" fraction="2.000000000000" units="cups">2 cups</amount> blueberries (clean & wash)</li></ul>' ] );
