ingredientsArray.push( [ 'CostanzoRecipes_Desserts_BananaBread', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> all-purpose flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> whole wheat flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 Tbsp" fraction="1.000000000000" units="Tbsp">1 Tbsp</amount> baking powder</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tsp" fraction="1.000000000000" units="tsp">1 tsp</amount> baking soda</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tsp" fraction="1.000000000000" units="tsp">1 tsp</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> (<amount original_amount="1 stick" fraction="1.000000000000" units="stick">1 stick</amount>) butter</li><li onclick="handleItemClick(this)"> <amount original_amount="2 eggs" fraction="2.000000000000" units="eggs">2 eggs</amount></li><li onclick="handleItemClick(this)"> <amount original_amount="⅓ cup" fraction="0.333333333333" units="cup">⅓ cup</amount> honey</li><li onclick="handleItemClick(this)"> <amount original_amount="1¼ cups" fraction="1.250000000000" units="cups">1¼ cups</amount> mashed ripe bananas (3-4)</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Desserts_BananaBread', '<p><ol><li onclick="handleItemClick(this)"> Preheat oven to 350 degrees F. Grease and flour a 9 x 5 x 2¼-inch loaf pan. </li><li onclick="handleItemClick(this)"> In a medium bowl or on <amount original_amount="a piece" fraction="1.000000000000" units="piece">a piece</amount> of waxed paper, stir together both flours, the baking powder, baking soda and salt with a whisk.</li><li onclick="handleItemClick(this)"> In a large bowl, cream the butter and the sugar until light and fluffy. Beat in the eggs until thoroughly combined. Beat in the honey.</li><li onclick="handleItemClick(this)"> Beat in the banana alternately with the flour mixture. Spoon into prepared pan. Bake 50 minutes or until a wooden pick inserted in the center comes out clean. Remove from the oven an turn onto a wire rack to cool. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 3000)</script></div></li><li onclick="handleItemClick(this)"> Optional: Add <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> chopped nuts to the batter.</li></ol>' ] );
