methodsArray.push( [ 'CostanzoRecipes_Desserts_AppleFritters', '<p><ol><li onclick="handleItemClick(this)"> In a large bowl, combine flour, granulated sugar and salt; mix well. Whisk in egg substitute, milk and extract; stir in apples. </li><li onclick="handleItemClick(this)"> Melt margarine in <amount original_amount="a large" fraction="1.000000000000" units="large">a large</amount> nonstick skillet over medium-high heat. Drop apple mixture into pan, making 8 even fritters. Cook until golden brown, about 2 to 3 minutes per side. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 120)</script><script>new CircularTimer("CircularTimer-1", 180)</script></div></li><li onclick="handleItemClick(this)"> Sift powdered sugar over top of fritters just before serving.  </li></ol>' ] );
