methodsArray.push( [ 'CostanzoRecipes_Desserts_AppleCrisp', '<p><ol><li onclick="handleItemClick(this)"> Preheat oven to 450 degrees. </li><li onclick="handleItemClick(this)"> Arrange apples in a greased square pan 8x8x2 inches. </li><li onclick="handleItemClick(this)"> Mix remaining ingredients and sprinkle over apples. </li><li onclick="handleItemClick(this)"> Bake until topping is golden brown and apples are tender, about 30 minutes. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 1800)</script></div></li><li onclick="handleItemClick(this)"> Serve warm and, if desired, with cream or ice cream. </li></ol>' ] );
