ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_WafflesVegan', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> (<amount original_amount="120 g" fraction="120.000000000000" units="g">120 g</amount>) all-purpose flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> baking powder</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> coconut sugar or brown sugar</li><li onclick="handleItemClick(this)"> ½ pinch salt </li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> oat or almond milk</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> (<amount original_amount="15 g" fraction="15.000000000000" units="g">15 g</amount>) melted coconut oil or canola oil</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> unsweetened apple sauce or an additional <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> (<amount original_amount="15 g" fraction="15.000000000000" units="g">15 g</amount>) melted coconut oil</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> natural vanilla extract</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_WafflesVegan', '<p><ol><li onclick="handleItemClick(this)"> Combine all ingredients for the vegan waffles in a medium bowl.</li><li onclick="handleItemClick(this)"> Using a hand whisk, stir well until all lumps are removed. <div class="tip">    <i>The batter will be quite thin. Don\'t worry, that\'s normal!</i>  </div></li><li onclick="handleItemClick(this)"> Heat your waffle maker and grease it with coconut oil or canola oil on both sides (top and bottom). </li><li onclick="handleItemClick(this)"> Pour the batter with a ladle into the waffle iron. <i>It depends on your waffle maker how much batter you need, so check the manufacturer instructions.</i>  </li><li onclick="handleItemClick(this)"> Close your waffle maker and bake the waffles for about 5 minutes until they\'re golden and crispy on the outside. Don\'t open the waffle maker too early. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 300)</script></div></li><li onclick="handleItemClick(this)"> Serve the waffles immediately with maple syrup and berries or other fruits. Enjoy! </li></ol>' ] );
