creditsArray.push( [ 'CostanzoRecipes_Breakfast_FirstWatchAMSuperfoodsBowl', '<p><ul><li onclick="handleItemClick(this)"> From <a href="https://twohealthykitchens.com/chia-pudding-coconut-milk/#mv-creation-101-jtr" target="_blank">https://twohealthykitchens.com/chia-pudding-coconut-milk/#mv-creation-101-jtr</a>  </li><li onclick="handleItemClick(this)"> date created: [2024-12-21 Sat]   </li><li onclick="handleItemClick(this)">[2024-12-20 Fri]  It comes pretty close to that tasty First Watch dish! I used canned coconut milk but feel I could probably have used oat milk. </li></ul>' ] );
