ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_CrispyOvenRoastedBreakfastPotatoes', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="2 pounds" fraction="2.000000000000" units="pounds">2 pounds</amount> new potatoes (or other small potato)</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> salt (for water)</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> baking soda</li><li onclick="handleItemClick(this)"> <amount original_amount="⅓ cup" fraction="0.333333333333" units="cup">⅓ cup</amount> olive oil</li><li onclick="handleItemClick(this)"> < <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> pepper</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="">½</amount>to <amount original_amount="1 teaspoon" fraction="1.000000000000" units="teaspoon">1 teaspoon</amount> each of the following spices: Turmeric, cayenne pepper, garlic powder, fajita seasoning, smoked paprika</li><li onclick="handleItemClick(this)"> 1 finely chopped jalapeno pepper, optional </li><li onclick="handleItemClick(this)"> ½ finely chopped red bell pepper, optional </li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_CrispyOvenRoastedBreakfastPotatoes', '<p><ol><li onclick="handleItemClick(this)"> Preheat oven to 450F. </li><li onclick="handleItemClick(this)"> Wash potatoes - but don\'t peel. Cut potato into fourths, or into bite-sized pieces. </li><li onclick="handleItemClick(this)"> Bring large pot of water to a boil, adding the salt and baking soda. Once water is boiling, add potato chunks. Boil for about 5 to 6 minutes, until potatoes are fork tender. Remove from heat and drain. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 300)</script><script>new CircularTimer("CircularTimer-1", 360)</script></div></li><li onclick="handleItemClick(this)"> In a large bowl, combine olive oil and spices. Add cooked potatoes and toss to ensure even coating.</li><li onclick="handleItemClick(this)"> Place potatoes on <amount original_amount="a large" fraction="1.000000000000" units="large">a large</amount> baking sheet. Place in oven and bake for 20 minutes. Remove from oven and use a spatula to gently move potatoes around and unstick any stuck potatoes, to ensure even roasting.<img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 1200)</script></div></li><li onclick="handleItemClick(this)"> Return potatoes to oven and roast for an additional 30 to 40 minutes, until potatoes are cooked to your liking. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-3\')" class="alarm-clock-icon"><div id="CircularTimer-3" class="circular-timer-container"><script>new CircularTimer("CircularTimer-3", 1800)</script><script>new CircularTimer("CircularTimer-3", 2400)</script></div></li></ol>' ] );
creditsArray.push( [ 'CostanzoRecipes_Breakfast_CrispyOvenRoastedBreakfastPotatoes', '<p><ul><li onclick="handleItemClick(this)"><a href="https://www.cheaprecipeblog.com/wp-json/mv-create/v1/creations/5/print" target="_blank">https://www.cheaprecipeblog.com/wp-json/mv-create/v1/creations/5/print</a>  </li><li onclick="handleItemClick(this)"> date created: [2020-01-18 Sat]   </li><li onclick="handleItemClick(this)">[2020-01-19 Sun]  Made for brunch with Mom, Lucille and John. Good, crispy. Used Penzy\'s Turkish spice. </li><li onclick="handleItemClick(this)">[2021-09-26 Sun]  Made for brunch with Heimburgs. These potatoes are good. But they <i>are</i> spicy. Consider going lighter on the cayenne if there are non-spice lovers. </li></ul>' ] );
