ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_CranberryMuffins', '<p> Preheat oven to <b>400 degrees</b> . <p><ul><li onclick="handleItemClick(this)"> <amount original_amount="⅔ cup" fraction="0.666666666666" units="cup">⅔ cup</amount> sugar</li><li onclick="handleItemClick(this)"> <amount original_amount="1¾ cup" fraction="1.750000000000" units="cup">1¾ cup</amount> flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 Tblsp" fraction="1.000000000000" units="Tblsp">1 Tblsp</amount> baking powder</li><li onclick="handleItemClick(this)"> dash salt </li><li onclick="handleItemClick(this)"> <amount original_amount="1 Tblsp" fraction="1.000000000000" units="Tblsp">1 Tblsp</amount> grated orange zest</li><li onclick="handleItemClick(this)"> <amount original_amount="1 egg" fraction="1.000000000000" units="egg">1 egg</amount></li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> buttermilk</li><li onclick="handleItemClick(this)"> <amount original_amount="⅓ cup" fraction="0.333333333333" units="cup">⅓ cup</amount> vegetable oil</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> rinsed cranberries</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_CranberryMuffins', '<p><ol><li onclick="handleItemClick(this)"> Mix together: <ul><li onclick="handleItemClick(this)"> sugar </li><li onclick="handleItemClick(this)"> flour </li><li onclick="handleItemClick(this)"> baking powder </li><li onclick="handleItemClick(this)"> salt </li></ul></li><li onclick="handleItemClick(this)"> In another bowl, whisk together: <ul><li onclick="handleItemClick(this)"> orange zest </li><li onclick="handleItemClick(this)"> egg </li><li onclick="handleItemClick(this)"> buttermilk </li><li onclick="handleItemClick(this)"> vegetable oil </li></ul></li><li onclick="handleItemClick(this)"> Mix cranberries into dry ingredients. Pour into liquid mixture and stir. </li><li onclick="handleItemClick(this)"> Spoon into paper-lined muffin tin about ⅔ full. </li><li onclick="handleItemClick(this)"> Bake at 400 degrees for 25 minutes. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 1500)</script></div></li></ol>' ] );
