ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_ButtermilkSyrup', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> unsalted butter</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> sugar</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> buttermilk</li><li onclick="handleItemClick(this)"> <amount original_amount="2 teaspoons" fraction="2.000000000000" units="teaspoons">2 teaspoons</amount> vanilla</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tablespoon" fraction="1.000000000000" units="tablespoon">1 tablespoon</amount> Light karo syrup</li><li onclick="handleItemClick(this)"> <amount original_amount="½ teaspoon" fraction="0.500000000000" units="teaspoon">½ teaspoon</amount> baking soda</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_ButtermilkSyrup', '<p><ol><li onclick="handleItemClick(this)"> In a large pot or saucepan over medium heat add the butter, sugar, buttermilk, vanilla and karo syrup. Heat until everything is well combined and bring to a boil.  </li><li onclick="handleItemClick(this)"> Quickly remove the pot from the heat and add the baking soda, place back over the heat, stirring constantly. The syrup will grow large and threaten to boil over which is why a large pot is essential. If it gets too close to the edge remove from the burner and continue stirring to bring it down. </li><li onclick="handleItemClick(this)"> Stir for 30 seconds over the heat (I often just take mine off so I don\'t risk boiling over) and serve immediately.  <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 30)</script></div></li></ol>' ] );
