ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_BlueberryButtermilkMuffins', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> buttermilk</li><li onclick="handleItemClick(this)"> <amount original_amount="½ cup" fraction="0.500000000000" units="cup">½ cup</amount> butter or margarine -- melted</li><li onclick="handleItemClick(this)"> <amount original_amount="2 eggs" fraction="2.000000000000" units="eggs">2 eggs</amount> -- beaten</li><li onclick="handleItemClick(this)"> <amount original_amount="2½ cups" fraction="2.500000000000" units="cups">2½ cups</amount> all-purpose flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> sugar</li><li onclick="handleItemClick(this)"> <amount original_amount="2½ teaspoons" fraction="2.500000000000" units="teaspoons">2½ teaspoons</amount> baking powder</li><li onclick="handleItemClick(this)"> <amount original_amount="¼ teaspoon" fraction="0.250000000000" units="teaspoon">¼ teaspoon</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="1½ cup" fraction="1.500000000000" units="cup">1½ cup</amount> Fresh Or Frozen Blueberries</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_BlueberryButtermilkMuffins', '<p><ol><li onclick="handleItemClick(this)"> In small bowl, combine buttermilk, butter and eggs until blended. In large bowl, combine flour, sugar, baking powder and salt. Make well in center. Add buttermilk mixture, stirring until flour mixture is just moistened. Fold in blueberries. Spoon batter into greased muffin cups, filling ⅔ full. Bake in preheated 400 degree oven 20 minutes or until tops are golden and wooden pick inserted in center comes out clean. Remove from pan. Serve warm or cool on wire rack. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 1200)</script></div></li><li onclick="handleItemClick(this)"> This recipe for Blueberry Buttermilk Muffins serves/makes 18 </li></ol>' ] );
