ingredientsArray.push( [ 'CostanzoRecipes_Breakfast_BestBranMuffins', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> whole wheat flour</li><li onclick="handleItemClick(this)"> <amount original_amount="1 tsp" fraction="1.000000000000" units="tsp">1 tsp</amount> baking soda</li><li onclick="handleItemClick(this)"> <amount original_amount="½ tsp" fraction="0.500000000000" units="tsp">½ tsp</amount> salt</li><li onclick="handleItemClick(this)"> <amount original_amount="1½ c" fraction="1.500000000000" units="c">1½ c</amount>. wheat bran</li><li onclick="handleItemClick(this)"> 3 Tbls	butter (or oil) </li><li onclick="handleItemClick(this)"> 2 Tbls	brown sugar </li><li onclick="handleItemClick(this)"> 2 Tbls	molasses </li><li onclick="handleItemClick(this)"> <amount original_amount="1 egg" fraction="1.000000000000" units="egg">1 egg</amount></li><li onclick="handleItemClick(this)"> <amount original_amount="1½ c" fraction="1.500000000000" units="c">1½ c</amount>.	buttermilk</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Breakfast_BestBranMuffins', '<p><ol><li onclick="handleItemClick(this)"> Preheat oven to 375 degrees. Grease pan. </li><li onclick="handleItemClick(this)"> Sift flour, soda and salt together. Stir in the bran. </li><li onclick="handleItemClick(this)"> Beat butter or oil and sugar and molasses together. Add egg and buttermilk. Mix dry ingredients into liquids. Place in the pan, about ¾ full. Bake muffins 15 to 20 minutes; or <amount original_amount="a loaf" fraction="1.000000000000" units="loaf">a loaf</amount> for about 30 minutes.<img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 900)</script><script>new CircularTimer("CircularTimer-1", 1200)</script><script>new CircularTimer("CircularTimer-1", 1800)</script></div></li><li onclick="handleItemClick(this)"> Alternative: use a 3" x 7" loaf. Makes a nice tea bread. </li></ol>' ] );
