ingredientsArray.push( [ 'CostanzoRecipes_Appetizers_HotPepperJelly', '<p><ul><li onclick="handleItemClick(this)"> <amount original_amount="2½ cups" fraction="2.500000000000" units="cups">2½ cups</amount> finely chopped red bell peppers</li><li onclick="handleItemClick(this)"> <amount original_amount="1¼ cups" fraction="1.250000000000" units="cups">1¼ cups</amount> finely chopped green bell peppers</li><li onclick="handleItemClick(this)"> <amount original_amount="¼ cup" fraction="0.250000000000" units="cup">¼ cup</amount> finely chopped jalapeno peppers</li><li onclick="handleItemClick(this)"> <amount original_amount="1 cup" fraction="1.000000000000" units="cup">1 cup</amount> apple cider vinegar</li><li onclick="handleItemClick(this)"> <amount original_amount="1 (1.75 ounce) package" fraction="1.000000000000" units="(1.75 ounce) package" implied_unintary="true">1 (1.75 ounce) package</amount> powdered pectin</li><li onclick="handleItemClick(this)"> <amount original_amount="5 cups" fraction="5.000000000000" units="cups">5 cups</amount> white sugar</li></ul>' ] );
methodsArray.push( [ 'CostanzoRecipes_Appetizers_HotPepperJelly', '<p><ol><li onclick="handleItemClick(this)"> Sterilize six 8-ounce canning jars and lids according to the manufacturer\'s instructions. </li><li onclick="handleItemClick(this)"> Heat water in a hot water canner. </li><li onclick="handleItemClick(this)"> Place bell peppers and jalapeño peppers in a large saucepan over high heat; stir in vinegar and fruit pectin.</li><li onclick="handleItemClick(this)"> Bring to a rolling boil, stirring constantly. </li><li onclick="handleItemClick(this)"> Stir in sugar; bring back to a rolling boil, stirring constantly. </li><li onclick="handleItemClick(this)"> Allow to boil for 1 minute, then remove from the heat and skim any foam off the top. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-1\')" class="alarm-clock-icon"><div id="CircularTimer-1" class="circular-timer-container"><script>new CircularTimer("CircularTimer-1", 60)</script></div></li><li onclick="handleItemClick(this)"> Quickly ladle jelly into sterile jars, filling to within ¼-inch of the tops. </li><li onclick="handleItemClick(this)"> Cover with flat lids and screw on rings tightly. </li><li onclick="handleItemClick(this)"> Place jars in the rack and slowly lower jars into the canner. </li><li onclick="handleItemClick(this)"> The water should cover the jars completely and should be hot but not boiling. </li><li onclick="handleItemClick(this)"> Bring water to a boil; cover the canner and process for 5 minutes. <img src="images/alarm-clock-icon.png" onclick="toggleElement(\'CircularTimer-2\')" class="alarm-clock-icon"><div id="CircularTimer-2" class="circular-timer-container"><script>new CircularTimer("CircularTimer-2", 300)</script></div></li></ol><p><div class="note"> This makes 6 (six) 8-ounce jars. </div>' ] );
