ingredients_array.push( [ 'CostanzoRecipes_Sides_WhiteCornTortillaSoup', '<h3 onclick="toggleSection(\'1259\')">For 5 servings:<span id="heading-ellipses-1259"></h3><div id=\'heading-content-1259\'><p><ul><li onclick="handleItemClick()">3 Tablespoon olive oil </li><li onclick="handleItemClick()">1½ tortillas cut into 1" squares </li><li onclick="handleItemClick()">1½ Tablespoon minced garlic </li><li onclick="handleItemClick()">2 Tablespoon minced white onions </li><li onclick="handleItemClick()">1½ teaspoon minced jalapeño pepper </li><li onclick="handleItemClick()">1 lb white corn kernels </li><li onclick="handleItemClick()">1½ lb chopped tomatoes </li><li onclick="handleItemClick()">⅓ C tomato paste </li><li onclick="handleItemClick()">2½ teaspoon ground cumin </li><li onclick="handleItemClick()">1 Tablespoon Kosher salt </li><li onclick="handleItemClick()">⅛ teaspoon ground white pepper </li><li onclick="handleItemClick()">½ teaspoon chili powder </li><li onclick="handleItemClick()">1½ cup water </li><li onclick="handleItemClick()">1 quart vegetable stock </li><li onclick="handleItemClick()">24 blue corn chips (optional garnish) </li><li onclick="handleItemClick()">2 cups shredded cheddar cheese (optional garnish) </li><li onclick="handleItemClick()">½ cup chopped fresh cilantro (optional garnish) </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1260\')">For 10 servings:<span id="heading-ellipses-1260"></h3><div id=\'heading-content-1260\'><p><ul><li onclick="handleItemClick()">6 Tablespoon olive oil </li><li onclick="handleItemClick()">3 tortillas cut into 1" squares </li><li onclick="handleItemClick()">3 Tablespoon minced garlic </li><li onclick="handleItemClick()">4 Tablespoon minced white onions </li><li onclick="handleItemClick()">3 teaspoon minced jalapeño pepper </li><li onclick="handleItemClick()">2 lb white corn kernels </li><li onclick="handleItemClick()">3 lb chopped tomatoes </li><li onclick="handleItemClick()">⅔ C tomato paste </li><li onclick="handleItemClick()">5 teaspoon ground cumin </li><li onclick="handleItemClick()">2 Tablespoon Kosher salt </li><li onclick="handleItemClick()">¼ teaspoon ground white pepper </li><li onclick="handleItemClick()">1 teaspoon chili powder </li><li onclick="handleItemClick()">3 cup water </li><li onclick="handleItemClick()">2 quart vegetable stock </li><li onclick="handleItemClick()">48 blue corn chips (optional garnish) </li><li onclick="handleItemClick()">4 cups shredded cheddar cheese (optional garnish) </li><li onclick="handleItemClick()">1 cup chopped fresh cilantro (optional garnish) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_WhiteCornTortillaSoup', '<p><ol><li onclick="handleItemClick()">Fry tortilla squares in olive oil until they begin to crisp. </li><li onclick="handleItemClick()">Add onion, garlic and jalapeño. </li><li onclick="handleItemClick()">Cook 1 - 2 minutes until onion becomes translucent. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1711\')" class="alarm-clock-icon"><div id="circular-timer-1711" class="circular-timer-container"><script>new CircularTimer("circular-timer-1711", 60)</script><script>new CircularTimer("circular-timer-1711", 120)</script></li><li onclick="handleItemClick()">Add half the corn and all other ingredients. </li><li onclick="handleItemClick()">Bring soup to a low, even boil. </li><li onclick="handleItemClick()">Boil for 5 minutes; then remove from heat and puree. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1712\')" class="alarm-clock-icon"><div id="circular-timer-1712" class="circular-timer-container"><script>new CircularTimer("circular-timer-1712", 300)</script></li><li onclick="handleItemClick()">Return soup to the burner, add reserved corn. </li><li onclick="handleItemClick()">Bring soup to boil again, carefully. </li><li onclick="handleItemClick()">Serve garnished with blue chips, cilantro, or sour cream. </li></ol>' ] );
