ingredients_array.push( [ 'CostanzoRecipes_Sides_VeganStuffedMushrooms', '<p><ul><li onclick="handleItemClick()">5 portobello mushrooms (see notes) </li><li onclick="handleItemClick()">1 tbsp oil </li><li onclick="handleItemClick()">1 small/medium (110 g) onion diced </li><li onclick="handleItemClick()">1 medium (100 g) pepper </li><li onclick="handleItemClick()">1 tsp onion powder </li><li onclick="handleItemClick()">1 tsp garlic powder </li><li onclick="handleItemClick()">1/2 tsp ground cumin </li><li onclick="handleItemClick()">Salt, ground pepper, and chili powder to taste </li><li onclick="handleItemClick()">2 cloves of garlic </li><li onclick="handleItemClick()">1 cup canned (175 g) chickpeas drained and rinsed </li><li onclick="handleItemClick()">1/3 cup (40 g) walnuts finely chopped </li><li onclick="handleItemClick()">1 heaped tbsp (30 g) tomato paste </li><li onclick="handleItemClick()">1 tbsp tamari or soy sauce </li><li onclick="handleItemClick()">1/2 tbsp balsamic vinegar </li><li onclick="handleItemClick()">4 tbsp dairy-free milk </li><li onclick="handleItemClick()">vegan cheese to taste </li><li onclick="handleItemClick()">Fresh herbs to garnish </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_VeganStuffedMushrooms', '<p><ol><li onclick="handleItemClick()">Preheat the oven to 360 F (180 C) and line a baking sheet with parchment paper. </li><li onclick="handleItemClick()">Remove the stems of the mushrooms and chop them into pieces. Set aside. </li><li onclick="handleItemClick()">Add the chickpeas to a bowl and mash them with a fork. </li><li onclick="handleItemClick()">Heat oil in a large skillet, add onion, pepper, and all spices. Stir to combine and cook for 3-4 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1709\')" class="alarm-clock-icon"><div id="circular-timer-1709" class="circular-timer-container"><script>new CircularTimer("circular-timer-1709", 180)</script><script>new CircularTimer("circular-timer-1709", 240)</script></li><li onclick="handleItemClick()">Also, stir in garlic, mashed chickpeas, and walnuts. </li><li onclick="handleItemClick()">Next add the tomato paste, tamari, balsamic vinegar, and dairy-free milk. Stir to combine. </li><li onclick="handleItemClick()">Finally, add the chopped mushroom stems and stir. Cook for a few more minutes, then set aside. </li><li onclick="handleItemClick()">Place the portobello mushrooms on the lined baking sheet and fill each mushroom with the filling until there is no filling left. </li><li onclick="handleItemClick()">Bake in the oven for about 10 minutes, then add vegan cheese of choice (I used my vegan cheese sauce) and bake for a further 10 minutes. Garnish with fresh herbs and enjoy! <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1710\')" class="alarm-clock-icon"><div id="circular-timer-1710" class="circular-timer-container"><script>new CircularTimer("circular-timer-1710", 600)</script><script>new CircularTimer("circular-timer-1710", 600)</script></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Sides_VeganStuffedMushrooms', '<p>IsVegan <p><ul><li onclick="handleItemClick()">From <a href="https://elavegan.com/wprm_print/18133" target="_blank">https://elavegan.com/wprm_print/18133</a> </li><li onclick="handleItemClick()">date created: [2022-07-04 Mon]  </li><li onclick="handleItemClick()"><2022-06-28 Tue> J made these but mistakenly used cannellini beans. They were tasty. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Sides_SweetPotatoAvocadoToasts', '<p><ul><li onclick="handleItemClick()">2 medium sweet potatoes </li><li onclick="handleItemClick()">1 ripe avocado, halved and seeded </li><li onclick="handleItemClick()">½ cup fresh or frozen corn kernels </li><li onclick="handleItemClick()">½ cup cooked black beans </li><li onclick="handleItemClick()">1 small tomato, finely chopped </li><li onclick="handleItemClick()">½ cup thinly sliced scallions </li><li onclick="handleItemClick()">2 tablespoons chopped fresh cilantro </li><li onclick="handleItemClick()">2 tablespoons lemon juice </li><li onclick="handleItemClick()">1 fresh jalapeño chile, seeded and finely chopped </li><li onclick="handleItemClick()">1 clove garlic, minced </li><li onclick="handleItemClick()">sea salt, to taste </li><li onclick="handleItemClick()">chipotle chile powder or smoked paprika </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_SweetPotatoAvocadoToasts', '<p><ol><li onclick="handleItemClick()">Preheat oven to 425°F. </li><li onclick="handleItemClick()">Prick the sweet potatoes in a few places with a fork. </li><li onclick="handleItemClick()">Bake 30 to 40 minutes, until a fork inserted in a sweet potatoes reaches the core with some resistance. (Do not cook until tender!) Remove sweet potatoes from oven and cool <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1707\')" class="alarm-clock-icon"><div id="circular-timer-1707" class="circular-timer-container"><script>new CircularTimer("circular-timer-1707", 1800)</script><script>new CircularTimer("circular-timer-1707", 2400)</script></li><li onclick="handleItemClick()">Scoop avocado flesh into a bowl and mash it. </li><li onclick="handleItemClick()">Stir in corn and black beans. </li><li onclick="handleItemClick()">Reserve about 1 tablespoon each of the tomato, scallions and cilantro; add the remaining tomato, scallions and cilantro to avocado mixture. </li><li onclick="handleItemClick()">Add lemon juice, jalapeño garlic and salt. Mix well. Taste and adjust seasonings if necessary.  </li><li onclick="handleItemClick()">Cut the sweet potatoes into twelve ½-inch-thick rounds. </li><li onclick="handleItemClick()">Toast them in a toaster oven until the edges are brown. (Alternatively, arrange rounds on a parchment-lined baking sheet and bake in a 425°F oven for 10 to 15 minutes or until edges are brown, turning once, halfway through baking.) <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1708\')" class="alarm-clock-icon"><div id="circular-timer-1708" class="circular-timer-container"><script>new CircularTimer("circular-timer-1708", 600)</script><script>new CircularTimer("circular-timer-1708", 900)</script></li><li onclick="handleItemClick()">Arrange toasted rounds on a platter. </li><li onclick="handleItemClick()">Top each round with a scoop of avocado mixture. </li><li onclick="handleItemClick()">Top with reserved tomato, scallions and cilantro. </li><li onclick="handleItemClick()">If desired, sprinkle lightly with chipotle powder or smoked paprika. </li></ol>' ] );
