ingredients_array.push( [ 'CostanzoRecipes_Sides_SummerFarroSalad', '<h3 onclick="toggleSection(\'1257\')">for Salad:<span id="heading-ellipses-1257"></h3><div id=\'heading-content-1257\'><p><ul><li onclick="handleItemClick()">3/4  cup uncooked farro </li><li onclick="handleItemClick()">1 pint strawberries hulled and quartered </li><li onclick="handleItemClick()">2 medium avocados diced </li><li onclick="handleItemClick()">3 cups loosely packed baby spinach roughly chopped </li><li onclick="handleItemClick()">1/2 cup Vegan crumbled feta cheese </li><li onclick="handleItemClick()">1/2  small red onion finely chopped </li><li onclick="handleItemClick()">1/4 cup chopped toasted walnuts </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1258\')">for Dressing<span id="heading-ellipses-1258"></h3><div id=\'heading-content-1258\'><p><ul><li onclick="handleItemClick()">3 tablespoons extra virgin olive oil** or aquafaba </li><li onclick="handleItemClick()">2 tablespoons balsamic vinegar </li><li onclick="handleItemClick()">1 tablespoons honey </li><li onclick="handleItemClick()">1 teaspoon Dijon mustard </li><li onclick="handleItemClick()">2 teaspoons poppy seeds </li><li onclick="handleItemClick()">1/4 teaspoon kosher salt </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_SummerFarroSalad', '<p><ol><li onclick="handleItemClick()">Prepare farro according to package instructions. Place in a serving bowl and let cool while you prepare the other ingredients. </li><li onclick="handleItemClick()">In a small bowl, whisk together all of the ingredients until combined: olive oil or aquafaba, balsamic vinegar, honey, mustard, poppy seeds, and salt. Taste and adjust seasoning as desired. </li><li onclick="handleItemClick()">Pour over the farro, then toss to coat. </li><li onclick="handleItemClick()">Add the remaining ingredients to the bowl with the farro: strawberries, avocados, spinach, feta, red onion, and walnuts, then toss gently to combine. Serve chilled or at room temperature. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Sides_SummerFarroSalad', '<p><ul><li onclick="handleItemClick()">From Patsy McCutcheon </li><li onclick="handleItemClick()">date created: [2023-06-24 Sat]  </li><li onclick="handleItemClick()">[2023-06-29 Thu] Made for Greg and Mary Ann. Left out the avocados but it was still tasty. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Sides_StracciatellaItalianEggDropSoup', '<p><ul><li onclick="handleItemClick()">4 Eggs </li><li onclick="handleItemClick()">6 cups <s class="conditional-space-after">chicken</s>vegetable broth </li><li onclick="handleItemClick()">⅓ cup fresh grated pecorino Romano </li><li onclick="handleItemClick()">½ cup thinly sliced baby spinach </li><li onclick="handleItemClick()">2 tablespoon Pork King Good Crumbs </li><li onclick="handleItemClick()">2 tablespoon sliced scallions </li><li onclick="handleItemClick()">2 tablespoon olive oil </li><li onclick="handleItemClick()">2 tablespoon diced shallot </li><li onclick="handleItemClick()">1 tablespoon minced garlic </li><li onclick="handleItemClick()">2 tsp Italian seasoning (spiceology) </li><li onclick="handleItemClick()">½ tsp turmeric </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_StracciatellaItalianEggDropSoup', '<p><ol><li onclick="handleItemClick()">Crack eggs and then add Italian seasoning, Pork King Good Crumbs and Pecorino Romano in a mixing bowl. Whisk ingredients thoroughly and set to the side. </li><li onclick="handleItemClick()">Add olive oil, diced shallots, and minced garlic to a saucepan over medium heat until they have softened. </li><li onclick="handleItemClick()">Add broth to the pan and bring to a simmer just below a boil. Add scallions and thinly sliced spinach and stir for about 20 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1706\')" class="alarm-clock-icon"><div id="circular-timer-1706" class="circular-timer-container"><script>new CircularTimer("circular-timer-1706", 20)</script></li><li onclick="handleItemClick()">Carefully pour in the egg mixture while whisking. Enjoy. </li></ol>' ] );
