ingredients_array.push( [ 'CostanzoRecipes_Sides_RefriedBeans', '<p><ul><li onclick="handleItemClick()">3 cups simmered black beans or pinto beans, with liquid (see recipe) </li><li onclick="handleItemClick()">2 tablespoons grapeseed or sunflower oil </li><li onclick="handleItemClick()">2 teaspoons cumin seeds, ground </li><li onclick="handleItemClick()">1 chipotle in adobo, seeded and minced (optional) </li><li onclick="handleItemClick()">Salt to taste </li><li onclick="handleItemClick()">1 teaspoon mild or hot chili powder (more to taste) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_RefriedBeans', '<p><ol><li onclick="handleItemClick()">Drain off about ½ cup of liquid from beans, retaining it in a separate bowl to use later for moistening beans, should they dry out. </li><li onclick="handleItemClick()">Heat oil over medium-high heat in a large, heavy nonstick frying pan and add ground cumin and chili. </li><li onclick="handleItemClick()">Cook, stirring over medium heat, for about a minute, until the spices begin to sizzle and cook. </li><li onclick="handleItemClick()">Add beans and optional chipotle. </li><li onclick="handleItemClick()">Fry beans, stirring and mashing with the back of a spoon, until they thicken and form a thin crust on the bottom of the pan. </li><li onclick="handleItemClick()">Stir up crust and mix into the beans. </li><li onclick="handleItemClick()">Cook until beans are thick but not dry, about 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1699\')" class="alarm-clock-icon"><div id="circular-timer-1699" class="circular-timer-container"><script>new CircularTimer("circular-timer-1699", 600)</script></li><li onclick="handleItemClick()">Add liquid you saved from the beans if they seem too dry. </li><li onclick="handleItemClick()">Taste refried beans and adjust salt (they probably won’t need any as the broth reduces when you refry them). </li></ol><p><div class="note">If you use canned beans, do not drain. The frying process will go more quickly. </div>' ] );
