ingredients_array.push( [ 'CostanzoRecipes_Sides_QuickRefrigeratorPickledCucumbers', '<p><ul><li onclick="handleItemClick()">5 small cucumbers </li><li onclick="handleItemClick()">1/4 tsp mustard seed, whole </li><li onclick="handleItemClick()">1/4 teaspoon dill, dried </li><li onclick="handleItemClick()">1/2 tsp black peppercorn </li><li onclick="handleItemClick()">2 garlic cloves, whole </li><li onclick="handleItemClick()">1 cup water </li><li onclick="handleItemClick()">1 cup white vinegar (can sub with apple cider vinegar) </li><li onclick="handleItemClick()">2 tbsp sugar </li><li onclick="handleItemClick()">1 tbsp salt </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_QuickRefrigeratorPickledCucumbers', '<p><ol><li onclick="handleItemClick()">Slice the cucumbers into uniform coins or spears (your choice!). You’re going to use around 3 to 5 (depending on the specific size) cucumbers in this recipe per 16 oz. jar. </li><li onclick="handleItemClick()">Add the cucumber slices, mustard seed, dill, peppercorn, and garlic into your jar. Don’t worry that the spices settle at the bottom, they will rise up again evenly distribute their flavor when you add the vinegar brine. </li><li onclick="handleItemClick()">Mix the vinegar, water, salt, and sugar in a stainless steel pan and bring to a boil. You’ll need to babysit this. </li><li onclick="handleItemClick()">As soon as it reaches a good boil, pour it over the cucumber mixture in the jar until they are just covered. Don’t let it over boil or some of the vinegar needed for the pickling process will boil out. </li><li onclick="handleItemClick()">Put the top on immediately and put into the refrigerator. Let flavors blend overnight for best results. </li></ol>' ] );
