methods_array.push( [ 'CostanzoRecipes_Sides_PickledOnions', '<p><ol><li onclick="handleItemClick()">Whisk first 3 ingredients and 1 cup water in a small bowl until sugar and salt dissolve. </li><li onclick="handleItemClick()">Place onion in a jar; pour vinegar mixture over. Let sit at room temperature for 1 hour. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1694\')" class="alarm-clock-icon"><div id="circular-timer-1694" class="circular-timer-container"><script>new CircularTimer("circular-timer-1694", 3600)</script></li><li onclick="handleItemClick()">Cover and chill. </li><li onclick="handleItemClick()">Drain onions before using. </li></ol>' ] );
