ingredients_array.push( [ 'CostanzoRecipes_Sides_OrangePistachioQuinoaSalad', '<p><ul><li onclick="handleItemClick()">¼ cup roasted pistachio nuts </li><li onclick="handleItemClick()">1 cup uncooked quinoa </li><li onclick="handleItemClick()">1½ cups water </li><li onclick="handleItemClick()">1 teaspoon ground cumin </li><li onclick="handleItemClick()">½ teaspoon ground coriander </li><li onclick="handleItemClick()">1 teaspoon sea salt </li><li onclick="handleItemClick()">⅛ teaspoon ground pepper </li><li onclick="handleItemClick()">½ cup fresh mint, chopped </li><li onclick="handleItemClick()">2 scallions, finely chopped </li><li onclick="handleItemClick()">2 tablespoons lemon juice </li><li onclick="handleItemClick()">2 tablespoons orange juice </li><li onclick="handleItemClick()">1 tablespoon orange zest </li><li onclick="handleItemClick()">2 tablespoons extra virgin olive oil </li><li onclick="handleItemClick()">½ cup raisins (dark or light) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_OrangePistachioQuinoaSalad', '<p><ol><li onclick="handleItemClick()">Place water and quinoa in a pan and bring to a boil, cover with a lid and lower heat to a simmer for 15 minutes or until all liquid is absorbed. (You can follow the recipe on the package.) <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1683\')" class="alarm-clock-icon"><div id="circular-timer-1683" class="circular-timer-container"><script>new CircularTimer("circular-timer-1683", 900)</script></li></ol><h3 onclick="toggleSection(\'1246\')">Dressing<span id="heading-ellipses-1246"></h3><div id=\'heading-content-1246\'><p><ol><li onclick="handleItemClick()">Combine the lemon juice, orange juice, zest and olive oil. </li><li onclick="handleItemClick()">Add the dressing about ½ hour before service and the pistachio nuts just before serving. </li></ol><p><div class="note">If the dressing is added too soon, the quinoa absorbs it and becomes dry. </div><p><div class="tip">If you have any leftovers you can add a little orange just and olive oil to moisten it. </div><p><div class="tip">Remove the pulp from the orange squeezed for the juice and add that to the salad. Or you can add 1 can of Mandarin orange sections. </div>' ] );
