credits_array.push( [ 'CostanzoRecipes_Sides_MexicanRiceRiceCooker', '<p>IsVegan <p><ul><li onclick="handleItemClick()">From <a href="https://noshingwiththenolands.com/rice-cooker-mexican-rice/" target="_blank">https://noshingwiththenolands.com/rice-cooker-mexican-rice/</a> </li><li onclick="handleItemClick()">date created: [2020-06-26 Fri]  </li><li onclick="handleItemClick()">[2020-06-26 Fri] T used half veggie broth, half water. And sprinkled a very light amount of smoked paprika instead of red pepper flakes. It was yummy (although T didn\'t get to taste any since she gave it away to provide meals to Trenton). </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Sides_LentilandBlackBeanSoupwithSmokedPaprika', '<p><ul><li onclick="handleItemClick()">1 tablespoon olive oil </li><li onclick="handleItemClick()">½ large onion chopped </li><li onclick="handleItemClick()">2 large stalks celery diced </li><li onclick="handleItemClick()">2 cloves garlic minced </li><li onclick="handleItemClick()">2 teaspoons smoked paprika </li><li onclick="handleItemClick()">2 bay leaves </li><li onclick="handleItemClick()">a 14-ounce can diced tomatoes </li><li onclick="handleItemClick()">1 cup dried brown lentils </li><li onclick="handleItemClick()">4¾ cup low-sodium vegetable (or chicken) broth, divided </li><li onclick="handleItemClick()">3 cups water </li><li onclick="handleItemClick()">a 14-ounce can black beans, drained and rinsed </li><li onclick="handleItemClick()">¼ cup chopped parsley </li><li onclick="handleItemClick()">salt and pepper to taste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_LentilandBlackBeanSoupwithSmokedPaprika', '<p><ol><li onclick="handleItemClick()">Heat olive oil in a large saucepan set over medium heat. </li><li onclick="handleItemClick()">Add onions and celery and cook, stirring occasionally, until the vegetables are beginning to soften, 6 to 7 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1677\')" class="alarm-clock-icon"><div id="circular-timer-1677" class="circular-timer-container"><script>new CircularTimer("circular-timer-1677", 360)</script><script>new CircularTimer("circular-timer-1677", 420)</script></li><li onclick="handleItemClick()">Add garlic, smoked paprika and bay leaves and saute for 30 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1678\')" class="alarm-clock-icon"><div id="circular-timer-1678" class="circular-timer-container"><script>new CircularTimer("circular-timer-1678", 30)</script></li><li onclick="handleItemClick()">Stir in diced tomatoes with juices, lentils, 3 cups broth and 3 cups water. </li><li onclick="handleItemClick()">Increase heat to medium-high and bring the mixture to a boil. </li><li onclick="handleItemClick()">Reduce the heat slightly and cook, stirring occasionally, until lentils are tender, 25 to 35 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1679\')" class="alarm-clock-icon"><div id="circular-timer-1679" class="circular-timer-container"><script>new CircularTimer("circular-timer-1679", 1500)</script><script>new CircularTimer("circular-timer-1679", 2100)</script></li><li onclick="handleItemClick()">Remove from the heat and let cool for about 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1680\')" class="alarm-clock-icon"><div id="circular-timer-1680" class="circular-timer-container"><script>new CircularTimer("circular-timer-1680", 600)</script></li><li onclick="handleItemClick()">Remove and discard the bay leaves. </li><li onclick="handleItemClick()">Transfer half of the lentil mixture, half of the black beans and ¾ cup broth to the bowl of a blender or food processor. Pulse until combined, but not pureed. It should be a chunky texture. </li><li onclick="handleItemClick()">Pour the blender mixture back into the lentils in the saucepan, along with the remaining 1 cup of broth and remaining black beans. Stir and reheat over medium heat. </li></ol>' ] );
