ingredients_array.push( [ 'CostanzoRecipes_Sides_LemonGarlicKaleSalad', '<p><ul><li onclick="handleItemClick()">2 cups sliced almonds (or other nuts) </li><li onclick="handleItemClick()">⅓ cup freshly squeezed lemon juice (from 2 to 4 lemons) </li><li onclick="handleItemClick()">Kosher salt </li><li onclick="handleItemClick()">1½ cups extra-virgin olive oil </li><li onclick="handleItemClick()">4 cloves garlic, crushed with the flat side of a knife, peeled and left whole </li><li onclick="handleItemClick()">10 to 12 ounces washed and dried kale leaves, thick stems removed (weight after trimming) </li><li onclick="handleItemClick()">1½ cups freshly grated Parmesan (<i class="conditional-space-after">optional</i>) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_LemonGarlicKaleSalad', '<p><ol><li onclick="handleItemClick()">In a toaster oven or skillet, toast almonds until golden brown and fragrant. Set aside to cool. </li><li onclick="handleItemClick()">In a bowl, combine lemon juice and 1 heaping teaspoon salt. Slowly whisk in olive oil. Add garlic cloves and set aside to steep. </li><li onclick="handleItemClick()">Working in batches, cut the kale into thin ribbons: gather a large handful of leaves, bunch together tightly, and use the other hand to slice into 1/4-inch-thick pieces. <div class="note">   This need not be done very precisely or neatly; the idea is to end up with a kind of slaw. </div><div class="note">   Recipe can be made up to this point 1 day ahead. Keep kale and dressing refrigerated separately. </div></li><li onclick="handleItemClick()">Place chopped kale in a very large bowl. Sprinkle surface with almonds and then with cheese, if using. Remove and discard garlic cloves from dressing. Pour half the dressing over the salad and toss. Taste for dressing and salt and add more as needed, tossing to coat thoroughly. Serve within 1 hour. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1676\')" class="alarm-clock-icon"><div id="circular-timer-1676" class="circular-timer-container"><script>new CircularTimer("circular-timer-1676", 3600)</script></li></ol>' ] );
