ingredients_array.push( [ 'CostanzoRecipes_Sides_HotandSourSoupWithTofu', '<p><ul><li onclick="handleItemClick()">8 cups mushroom, vegetable stock </li><li onclick="handleItemClick()">2 sticks celery, roughly chopped </li><li onclick="handleItemClick()">½ head fennel, roughly chopped </li><li onclick="handleItemClick()">1 large leek, halved lengthwise </li><li onclick="handleItemClick()">3-inch piece ginger, peeled and sliced </li><li onclick="handleItemClick()">1½ teaspoons freshly ground white pepper, plus more as needed </li><li onclick="handleItemClick()">1 ½ teaspoon toasted sesame oil </li><li onclick="handleItemClick()">¼ cup soy sauce, plus more as needed </li><li onclick="handleItemClick()">2 tablespoons grapeseed oil </li><li onclick="handleItemClick()">¼ teaspoon celery seeds </li><li onclick="handleItemClick()">2 cups mushrooms, such as a mix of shiitake and fresh wood ear </li><li onclick="handleItemClick()">Kosher salt </li><li onclick="handleItemClick()">4 ounces firm tofu, cutinto ¾-inch cubes </li><li onclick="handleItemClick()">½ cup cooked, shredded crab meat(optional) </li><li onclick="handleItemClick()">¼ cup distilled white vinegar, plus more as needed </li><li onclick="handleItemClick()">3 tablespoons Chinese black vinegar, plus more as needed </li><li onclick="handleItemClick()">3 tablespoons sliced scallions,to garnish </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_HotandSourSoupWithTofu', '<p><ol><li onclick="handleItemClick()">In a large pot over medium-high heat, combine stock, celery, fennel, leeks, ginger, white pepper, sesame oil and soy sauce. </li><li onclick="handleItemClick()">Simmer until aromatics infuse broth, 20 minutes. Meanwhile, set a wok or a large frying pan over medium heat. Add grapeseed oil and celery seeds. Cook until fragrant, about 30 seconds, then add mushrooms and a pinch of salt. Cook until mushrooms are tender and lightly browned, about 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1667\')" class="alarm-clock-icon"><div id="circular-timer-1667" class="circular-timer-container"><script>new CircularTimer("circular-timer-1667", 1200)</script><script>new CircularTimer("circular-timer-1667", 30)</script><script>new CircularTimer("circular-timer-1667", 300)</script></li><li onclick="handleItemClick()">Strain broth and return to pot, discarding aromatics. Add white and black vinegars to broth. Taste and add more vinegar, white pepper and/or soy sauce as needed. Divide mushrooms, tofu and crab, if using, among 4 bowls, and ladle hot broth over each serving. Garnish with scallions. </li></ol>' ] );
