ingredients_array.push( [ 'CostanzoRecipes_Sides_HoneyGarlicCauliflower', '<p><ul><li onclick="handleItemClick()">1 c. all-purpose flour </li><li onclick="handleItemClick()">2 c. panko breadcrumbs </li><li onclick="handleItemClick()">3 large eggs, beaten </li><li onclick="handleItemClick()">1 head cauliflower, chopped into bite-size florets </li><li onclick="handleItemClick()">⅓ c. honey </li><li onclick="handleItemClick()">⅓ c. soy sauce </li><li onclick="handleItemClick()">2 garlic cloves, minced </li><li onclick="handleItemClick()">Juice of 1 lime </li><li onclick="handleItemClick()">1 tbsp. sriracha </li><li onclick="handleItemClick()">¼ c. water </li><li onclick="handleItemClick()">2 tsp. corn starch </li><li onclick="handleItemClick()">¼ c. Sliced scallions </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_HoneyGarlicCauliflower', '<p><ol><li onclick="handleItemClick()">Preheat oven to 400° and line a large baking sheet with foil. </li><li onclick="handleItemClick()">In a large bowl, combine flour and cauliflower, toss until fully coated. </li><li onclick="handleItemClick()">Set up a dredging station: In one bowl, add panko breadcrumbs and in another bowl whisk eggs and add 2 tablespoons water. </li><li onclick="handleItemClick()">Dip cauliflower in beaten eggs, then panko until fully coated. </li><li onclick="handleItemClick()">Transfer to a prepared baking sheet and season generously with salt and pepper. </li><li onclick="handleItemClick()">Bake until golden and crispy, 20 to 25 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1664\')" class="alarm-clock-icon"><div id="circular-timer-1664" class="circular-timer-container"><script>new CircularTimer("circular-timer-1664", 1200)</script><script>new CircularTimer("circular-timer-1664", 1500)</script></li><li onclick="handleItemClick()">Meanwhile, make sauce: <ul><li onclick="handleItemClick()">In a small bowl, whisk together cornstarch and water, until the cornstarch dissolves completely. Set aside. </li><li onclick="handleItemClick()">Combine soy sauce, honey, garlic, lime juice and Sriracha in a small saucepan over medium heat. </li></ul></li><li onclick="handleItemClick()">When the mixture reaches a boil, reduce heat and add the cornstarch mixture. </li><li onclick="handleItemClick()">Bring to simmer again and cook until sauce thickens, about 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1665\')" class="alarm-clock-icon"><div id="circular-timer-1665" class="circular-timer-container"><script>new CircularTimer("circular-timer-1665", 120)</script></li><li onclick="handleItemClick()">Toss cooked cauliflower in sauce until evenly coated. </li><li onclick="handleItemClick()">Return the cauliflower to baking sheet and broil for 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1666\')" class="alarm-clock-icon"><div id="circular-timer-1666" class="circular-timer-container"><script>new CircularTimer("circular-timer-1666", 120)</script></li><li onclick="handleItemClick()">Garnish with scallions and serve immediately. </li></ol>' ] );
