ingredients_array.push( [ 'CostanzoRecipes_Sides_HollandaiseSauce', '<h3 onclick="toggleSection(\'1238\')">Ingredients:<span id="heading-ellipses-1238"></h3><div id=\'heading-content-1238\'><p><ul><li onclick="handleItemClick()">4 egg yolks </li><li onclick="handleItemClick()">1 tablespoon freshly squeezed lemon juice </li><li onclick="handleItemClick()">½ cup unsalted butter, melted (1 stick) </li><li onclick="handleItemClick()">Pinch cayenne </li><li onclick="handleItemClick()">Pinch salt </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_HollandaiseSauce', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1239\')">Method:<span id="heading-ellipses-1239"></h3><div id=\'heading-content-1239\'><p><ol><li onclick="handleItemClick()">Vigorously whisk the egg yolks and lemon juice together in a stainless steel bowl and until the mixture is thickened and doubled in volume. </li><li onclick="handleItemClick()">Place the bowl over a saucepan containing barely simmering water (or use a double boiler,) the water should not touch the bottom of the bowl. </li><li onclick="handleItemClick()">Continue to whisk rapidly. Be careful not to let the eggs get too hot or they will scramble. </li><li onclick="handleItemClick()">Slowly drizzle in the melted butter and continue to whisk until the sauce is thickened and doubled in volume. </li><li onclick="handleItemClick()">Remove from heat, whisk in cayenne and salt. </li><li onclick="handleItemClick()">Cover and place in a warm spot until ready to use for the eggs benedict. If the sauce gets too thick, whisk in a few drops of warm water before serving. </li></ol>' ] );
