methods_array.push( [ 'CostanzoRecipes_Sides_HollandaiseSauce', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1243\')">Method:<span id="heading-ellipses-1243"></h3><div id=\'heading-content-1243\'><p><ol><li onclick="handleItemClick()">Stir 2 egg yolks, slightly beaten, and 3 tablespoons lemon juice vigorously in 1-quart saucepan with wooden spoon. </li><li onclick="handleItemClick()">Add ¼ cup butter. Heat over very low heat, stirring constantly, until butter is melted. </li><li onclick="handleItemClick()">Add ¼ cup butter, stirring vigorously until butter is melted and sauce thickens. </li><li onclick="handleItemClick()">(Be sure butter melts slowly; this give eggs time to cook and thicken sauce without curdling.) Serve hot or at room temperature.  </li></ol>' ] );
