ingredients_array.push( [ 'CostanzoRecipes_Sides_HealthSalad', '<p><ul><li onclick="handleItemClick()">1 medium green cabbage, shredded </li><li onclick="handleItemClick()">2 carrots, shredded </li><li onclick="handleItemClick()">1 green pepper, sliced paper thin in small strips </li><li onclick="handleItemClick()">2 kirby cucumbers, peeled and sliced paper thin </li><li onclick="handleItemClick()">1 onion, sliced paper thin </li><li onclick="handleItemClick()">¼ cup sugar   </li><li onclick="handleItemClick()">⅛ cup water </li><li onclick="handleItemClick()">¼ cup vegetable oil </li><li onclick="handleItemClick()">½ cup apple cider vinegar </li><li onclick="handleItemClick()">¼ teaspoon sea salt </li><li onclick="handleItemClick()">1 teaspoon oregano </li></ul>' ] );
