ingredients_array.push( [ 'CostanzoRecipes_Sides_HabaneroHotSauce', '<p><ul><li onclick="handleItemClick()">5 garlic cloves, unpeeled </li><li onclick="handleItemClick()">½ cup peeled, roughly chopped carrot (you\'ll need 1 medium carrot) </li><li onclick="handleItemClick()">½ cup roughly chopped white onion (you\'ll need about half of a small onion) </li><li onclick="handleItemClick()">12 medium (about 5 ounces) orange habanero chiles, stemmed </li><li onclick="handleItemClick()">1 cup apple cider vinegar </li><li onclick="handleItemClick()">About 2 teaspoons Salt </li><li onclick="handleItemClick()">¼ teaspoon Sugar </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_HabaneroHotSauce', '<p><ol><li onclick="handleItemClick()">Roast the garlic in a skillet over medium heat, turning regularly until soft and blackened in spots, 10 to 15 minutes. Cool and peel. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1661\')" class="alarm-clock-icon"><div id="circular-timer-1661" class="circular-timer-container"><script>new CircularTimer("circular-timer-1661", 600)</script><script>new CircularTimer("circular-timer-1661", 900)</script></li><li onclick="handleItemClick()">In a small saucepan, combine the carrot, onion and habanero chiles with the vinegar and 1 cup water. Partially cover and simmer over medium-low heat until the carrots are thoroughly tender, about 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1662\')" class="alarm-clock-icon"><div id="circular-timer-1662" class="circular-timer-container"><script>new CircularTimer("circular-timer-1662", 600)</script></li><li onclick="handleItemClick()">Pour into a blender jar, add the roasted garlic, salt and sugar. Blend until smooth. Thin with a little additional water if you think your hot sauce is too thick. Taste and season with additional salt if you think necessary. </li><li onclick="handleItemClick()">Pour into jars or bottles and store in the refrigerator until you\'re ready to add some dazzle to a dish. </li></ol>' ] );
