ingredients_array.push( [ 'CostanzoRecipes_Sides_GreekSaladwithDressing', '<h3 onclick="toggleSection(\'1234\')">Greek Vinaigrette<span id="heading-ellipses-1234"></h3><div id=\'heading-content-1234\'><p><ul><li onclick="handleItemClick()">½ cup olive oil </li><li onclick="handleItemClick()">½ cup red wine vinegar </li><li onclick="handleItemClick()">2 tbsp lemon juice </li><li onclick="handleItemClick()">4 cloves garlic , minced </li><li onclick="handleItemClick()">1 tbsp dry oregano </li><li onclick="handleItemClick()">1 tsp salt </li><li onclick="handleItemClick()">½ tsp black pepper </li><li onclick="handleItemClick()">1 tsp sugar </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1235\')">Salad<span id="heading-ellipses-1235"></h3><div id=\'heading-content-1235\'><p><ul><li onclick="handleItemClick()">3 tomatoes </li><li onclick="handleItemClick()">1 English cucumber </li><li onclick="handleItemClick()">2 sweet peppers (red, yellow or orange) </li><li onclick="handleItemClick()">1 red onion </li><li onclick="handleItemClick()">1 cup feta cheese , crumbled </li><li onclick="handleItemClick()">1 ½ cups black olives </li><li onclick="handleItemClick()">1 large romaine lettuce </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_GreekSaladwithDressing', '<p><ol><li onclick="handleItemClick()">Make the vinaigrette by mixing the oil, vinegar, lemon juice, garlic, oregano, salt, pepper and sugar. Let the dressing marinate for at least 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1659\')" class="alarm-clock-icon"><div id="circular-timer-1659" class="circular-timer-container"><script>new CircularTimer("circular-timer-1659", 1800)</script></li><li onclick="handleItemClick()">Chop up vegetables into large pieces. </li><li onclick="handleItemClick()">Stir the salad vinaigrette in a large bowl with the chopped vegetables (minus the romaine lettuce) and olives. </li><li onclick="handleItemClick()">Let the salad marinate at least 1 hour before serving. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1660\')" class="alarm-clock-icon"><div id="circular-timer-1660" class="circular-timer-container"><script>new CircularTimer("circular-timer-1660", 3600)</script></li><li onclick="handleItemClick()">Just prior to serving, toss in the chopped romaine lettuce leaves. </li><li onclick="handleItemClick()">Sprinkle feta cheese on top. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Sides_GreekSaladwithDressing', '<p><ul><li onclick="handleItemClick()"><a href="https://www.theblackpeppercorn.com/homemade-greek-salad-with-dressing/#recipe" target="_blank">https://www.theblackpeppercorn.com/homemade-greek-salad-with-dressing/#recipe</a> </li><li onclick="handleItemClick()">[2022-12-23 Fri] Made for MaryAnn\'s birthday. A tasty dressing but while I used only ¾ of a what it made on the salad, it still seemed like too much. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Sides_GreekSaladDressing', '<p><ul><li onclick="handleItemClick()">1/4 cup olive oil </li><li onclick="handleItemClick()">1/2 cup red wine vinegar </li><li onclick="handleItemClick()">juice of one lemon </li><li onclick="handleItemClick()">4 cloves garlic , minced </li><li onclick="handleItemClick()">1 tbsp dried oregano leaves </li><li onclick="handleItemClick()">salt </li><li onclick="handleItemClick()">black pepper </li><li onclick="handleItemClick()">1 tsp sugar or honey </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_GreekSaladDressing', '<p><ol><li onclick="handleItemClick()">Make the dressing by mixing the oil, vinegar, lemon juice, garlic, oregano, salt, pepper and sugar.  </li><li onclick="handleItemClick()">Let the dressing marinate for at least 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1658\')" class="alarm-clock-icon"><div id="circular-timer-1658" class="circular-timer-container"><script>new CircularTimer("circular-timer-1658", 1800)</script></li></ol>' ] );
