ingredients_array.push( [ 'CostanzoRecipes_Sides_FrozenFoodProcessorPesto', '<p><ul><li onclick="handleItemClick()">2 cups firmly packed basil leaves </li><li onclick="handleItemClick()">⅓ cup olive oil </li><li onclick="handleItemClick()">2 tablespoons pine nuts </li><li onclick="handleItemClick()">2 cloves peeled garlic </li><li onclick="handleItemClick()">⅔ cup parmesan cheese </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_FrozenFoodProcessorPesto', '<p><ol><li onclick="handleItemClick()">Blend all ingredients in a food processor until smoot, stopping to scrape sides from time to time. </li><li onclick="handleItemClick()">Ladle mixture into ice cube trays. </li><li onclick="handleItemClick()">Top off with a little olive oil. </li><li onclick="handleItemClick()">Cover with plastic wrap and freeze. </li><li onclick="handleItemClick()">When solid, empty trays into plastic bags and store in freezer. </li><li onclick="handleItemClick()">When ready to prepare pasta, gently thaw the desired amount of pesto (4 cubes per ½ pound of pasta). </li><li onclick="handleItemClick()">When at rom temperature, add 1 tablespoons parmesan cheese to each cube. </li><li onclick="handleItemClick()">Place in serving dish. </li><li onclick="handleItemClick()">Boil water for pasta. </li><li onclick="handleItemClick()">Add a couple of teaspoons of the pasta’s boiling water and stir until smooth. </li><li onclick="handleItemClick()">Toss pasta in the sauce and serve immediately. <div class="note">    Yield: 12 tablespoons (cubes) or enough for 1- 1½ pounds of pasta. </div><div class="warning">    Never, never, never heat or reheat pesto sauce. It will become bitter, so no leftovers!!! </div></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Sides_FrozenFoodProcessorPesto', '<p><ul><li onclick="handleItemClick()">From New York Times </li><li onclick="handleItemClick()">date created: [2021-07-08 Thu]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Sides_FriedRice', '<p><ul><li onclick="handleItemClick()">3 tablespoons butter, divided </li><li onclick="handleItemClick()">2 eggs, whisked </li><li onclick="handleItemClick()">2 medium carrots, peeled and diced </li><li onclick="handleItemClick()">1 small white onion, diced </li><li onclick="handleItemClick()">½ cup frozen peas </li><li onclick="handleItemClick()">3 cloves garlic, minced </li><li onclick="handleItemClick()">salt and black pepper </li><li onclick="handleItemClick()">4 cups cooked and chilled rice (I prefer short-grain white rice) </li><li onclick="handleItemClick()">3 green onions, thinly sliced </li><li onclick="handleItemClick()">3–4 tablespoons soy sauce, or more to taste </li><li onclick="handleItemClick()">2 teaspoons oyster sauce (optional) </li><li onclick="handleItemClick()">½ teaspoon toasted sesame oil </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_FriedRice', '<p><ol><li onclick="handleItemClick()">Heat ½ tablespoon of butter in a large sauté pan over medium-high heat until melted. </li><li onclick="handleItemClick()">Add egg, and cook until scrambled, stirring occasionally. </li><li onclick="handleItemClick()">Remove egg, and transfer to a separate plate. </li><li onclick="handleItemClick()">Add an additional 1 tablespoon butter to the pan and heat until melted. </li><li onclick="handleItemClick()">Add carrots, onion, peas and garlic, and season with a generous pinch of salt and pepper. </li><li onclick="handleItemClick()">Sauté for about 5 minutes or until the onion and carrots are soft. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1656\')" class="alarm-clock-icon"><div id="circular-timer-1656" class="circular-timer-container"><script>new CircularTimer("circular-timer-1656", 300)</script></li><li onclick="handleItemClick()">Increase heat to high, add in the remaining 1½ tablespoons of butter, and stir until melted. </li><li onclick="handleItemClick()">Immediately add the rice, green onions, soy sauce and oyster sauce (if using), and stir until combined. </li><li onclick="handleItemClick()">Continue sautéing for an additional 3 minutes to fry the rice, stirring occasionally. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1657\')" class="alarm-clock-icon"><div id="circular-timer-1657" class="circular-timer-container"><script>new CircularTimer("circular-timer-1657", 180)</script><div class="tip">    I like to let the rice rest for a bit between stirs so that it can crisp up on the bottom. </div></li><li onclick="handleItemClick()">Add in the eggs and stir to combine. </li><li onclick="handleItemClick()">Remove from heat, and stir in the sesame oil until combined. </li><li onclick="handleItemClick()">Taste and season with extra soy sauce, if needed. </li><li onclick="handleItemClick()">Serve immediately, or refrigerate in a sealed container for up to 3 days. </li></ol>' ] );
