ingredients_array.push( [ 'CostanzoRecipes_Sides_EscaroleBeanSoup', '<p><ul><li onclick="handleItemClick()">2 teaspoons vegetable oil </li><li onclick="handleItemClick()">¾ cup chopped onions </li><li onclick="handleItemClick()">1 clove garlic, minced </li><li onclick="handleItemClick()">One 14½ ounce can whole peeled tomatoes, undrained </li><li onclick="handleItemClick()">4 cups vegetable broth </li><li onclick="handleItemClick()">4 cups lightly packed, bite-sized pieces, escarole (or other green) </li><li onclick="handleItemClick()">1 cup cooked cannellini beans (cooked from dry or canned, drained) or use chickpeas or kidney beans </li><li onclick="handleItemClick()">¼ cup bowtie pasta (or other small pasta such as orzo) </li><li onclick="handleItemClick()">1 teaspoon sugar </li><li onclick="handleItemClick()">½ teaspoon oregano </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_EscaroleBeanSoup', '<p><ol><li onclick="handleItemClick()">In a 3-quart saucepan, heat the oil over medium-high heat. </li><li onclick="handleItemClick()">Add the onions and garlic; cook, stirring until softened, about 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1654\')" class="alarm-clock-icon"><div id="circular-timer-1654" class="circular-timer-container"><script>new CircularTimer("circular-timer-1654", 120)</script></li><li onclick="handleItemClick()">Add the tomatoes with liquid and break with up with the back of a spoon. </li><li onclick="handleItemClick()">Add the broth and bring to a boil. </li><li onclick="handleItemClick()">Add the remaining ingredients; return to a boil. </li><li onclick="handleItemClick()">Reduce heat and simmer, uncovered, 10 minutes or until pasta is tender. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1655\')" class="alarm-clock-icon"><div id="circular-timer-1655" class="circular-timer-container"><script>new CircularTimer("circular-timer-1655", 600)</script></li></ol>' ] );
