ingredients_array.push( [ 'CostanzoRecipes_Sides_CrispySaltandVinegarPotatoes', '<p><ul><li onclick="handleItemClick()">2 pounds baby Yukon Gold potatoes, halved, quartered if large </li><li onclick="handleItemClick()">1 cup plus 2 Tbsp. distilled white vinegar </li><li onclick="handleItemClick()">1 tablespoon kosher salt, plus more </li><li onclick="handleItemClick()">2 tablespoons unsalted butter </li><li onclick="handleItemClick()">Freshly ground black pepper </li><li onclick="handleItemClick()">2 tablespoons chopped fresh chives </li><li onclick="handleItemClick()">Flaky sea salt (such as Maldon) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_CrispySaltandVinegarPotatoes', '<p><ol><li onclick="handleItemClick()">Combine potatoes, 1 cup vinegar, and 1 Tbsp. kosher salt in a medium saucepan; add water to cover by 1”. </li><li onclick="handleItemClick()">Bring to a boil, reduce heat, and simmer until potatoes are tender, 20–25 minutes; drain and pat dry. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1652\')" class="alarm-clock-icon"><div id="circular-timer-1652" class="circular-timer-container"><script>new CircularTimer("circular-timer-1652", 1500)</script></li><li onclick="handleItemClick()">Heat butter in a large skillet over medium-high heat. </li><li onclick="handleItemClick()">Add potatoes; season with kosher salt and pepper. </li><li onclick="handleItemClick()">Cook, tossing occasionally, until golden brown and crisp, 8–10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1653\')" class="alarm-clock-icon"><div id="circular-timer-1653" class="circular-timer-container"><script>new CircularTimer("circular-timer-1653", 600)</script></li><li onclick="handleItemClick()">Drizzle with remaining 2 Tbsp. vinegar. </li><li onclick="handleItemClick()">Serve topped with chives and sea salt. </li></ol>' ] );
