ingredients_array.push( [ 'CostanzoRecipes_Sides_ChipotleRanchDressing', '<h3 onclick="toggleSection(\'1232\')">version 1<span id="heading-ellipses-1232"></h3><div id=\'heading-content-1232\'><p><ul><li onclick="handleItemClick()">1 cup mayonnaise </li><li onclick="handleItemClick()">1 cup sour cream </li><li onclick="handleItemClick()">1 cup buttermilk </li><li onclick="handleItemClick()">1½ teaspoons granulated garlic </li><li onclick="handleItemClick()">1 teaspoon garlic powder </li><li onclick="handleItemClick()">1½ teaspoons onion powder </li><li onclick="handleItemClick()">¼ cup dried parsley ( I used fresh) </li><li onclick="handleItemClick()">1½ teaspoons dried chipotle powder </li><li onclick="handleItemClick()">1½ teaspoons lime juice </li><li onclick="handleItemClick()">1½ teaspoons salt </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1233\')">version 2:<span id="heading-ellipses-1233"></h3><div id=\'heading-content-1233\'><p><ul><li onclick="handleItemClick()">½ cup mayo (or use sour cream, vegan mayo “Veganaise” – or make with silken tofu, see notes below) </li><li onclick="handleItemClick()">1 tablespoon water (for a thinner sauce) </li><li onclick="handleItemClick()">1 - 2 teaspoons lime juice (to taste) </li><li onclick="handleItemClick()">⅛ teaspoon salt, more to taste </li><li onclick="handleItemClick()">½ teaspoon chili powder </li><li onclick="handleItemClick()">1 teaspoon smoked paprika </li><li onclick="handleItemClick()">⅛ - ¼ teaspoon chipotle powder – you can also use one canned chipotle pepper (blending it with the mayo in a blender) or just stir in a teaspoon or two of the spicy smoky adobo sauce from the can. Start conservatively, adding more to taste. </li></ul>' ] );
