ingredients_array.push( [ 'CostanzoRecipes_Sides_ChiliSauce', '<p><ul><li onclick="handleItemClick()">1 (8-ounce) can tomato sauce </li><li onclick="handleItemClick()">¼ cup packed light or dark brown sugar </li><li onclick="handleItemClick()">3 tablespoons apple cider vinegar </li><li onclick="handleItemClick()">2 tablespoons tomato paste </li><li onclick="handleItemClick()">1 teaspoon Worcestershire sauce </li><li onclick="handleItemClick()">1 teaspoon smoked paprika </li><li onclick="handleItemClick()">½ teaspoon garlic powder </li><li onclick="handleItemClick()">½ teaspoon kosher salt </li><li onclick="handleItemClick()">¼ teaspoon freshly ground black pepper </li><li onclick="handleItemClick()">¼ teaspoon ground allspice </li><li onclick="handleItemClick()">⅛ teaspoon ground cinnamon </li><li onclick="handleItemClick()">⅛ teaspoon ground cloves </li><li onclick="handleItemClick()">⅛ teaspoon cayenne pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_ChiliSauce', '<p><ol><li onclick="handleItemClick()">Place: <ul><li onclick="handleItemClick()">1 (8-ounce) can tomato sauce </li><li onclick="handleItemClick()">¼ cup packed brown sugar </li><li onclick="handleItemClick()">3 tablespoons apple cider vinegar </li><li onclick="handleItemClick()">2 tablespoons tomato paste </li><li onclick="handleItemClick()">1 teaspoon Worcestershire sauce </li><li onclick="handleItemClick()">1 teaspoon smoked paprika </li><li onclick="handleItemClick()">½ teaspoon garlic powder </li><li onclick="handleItemClick()">½ teaspoon kosher salt </li><li onclick="handleItemClick()">¼ teaspoon black pepper </li><li onclick="handleItemClick()">¼ teaspoon ground allspice </li><li onclick="handleItemClick()">⅛ teaspoon ground cinnamon </li><li onclick="handleItemClick()">⅛ teaspoon ground cloves </li><li onclick="handleItemClick()">and ⅛ teaspoon cayenne pepper in a small saucepan and stir to combine. </li></ul></li><li onclick="handleItemClick()">Bring to a gentle boil over medium-high heat. Reduce the heat to low and simmer for 10 minutes <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1649\')" class="alarm-clock-icon"><div id="circular-timer-1649" class="circular-timer-container"><script>new CircularTimer("circular-timer-1649", 600)</script></li><li onclick="handleItemClick()">stirring occasionally. The sauce will turn a deeper, richer shade of red. </li><li onclick="handleItemClick()">Remove from the heat and let cool completely. </li><li onclick="handleItemClick()">Transfer to an airtight container and refrigerate for up to 1 week. </li></ol>' ] );
