ingredients_array.push( [ 'CostanzoRecipes_Sides_BuffaloBites', '<h3 onclick="toggleSection(\'1230\')">Buffalo Bites:<span id="heading-ellipses-1230"></h3><div id=\'heading-content-1230\'><p><ul><li onclick="handleItemClick()">1 16-ounce container firm tofu (I used Organic firm from Trader Joe’s – the one in the pink package) </li><li onclick="handleItemClick()">3/4 cup Frank’s Red Hot Buffalo Sauce, divided (½ + ¼ cup) </li><li onclick="handleItemClick()">1/4 cup water </li><li onclick="handleItemClick()">1 tablespoon + 1 teaspoon (vegan) butter, divided </li><li onclick="handleItemClick()">2 tablespoons cornstarch </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1231\')">Dipping Sauce (or sub Vegan Ranch):<span id="heading-ellipses-1231"></h3><div id=\'heading-content-1231\'><p><ul><li onclick="handleItemClick()">6 ounces unsweetened Greek-style non-dairy yogurt </li><li onclick="handleItemClick()">1 teaspoon seasoning blend like Ranch seasonings (I used Trader Joe’s 21 seasoning salute) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_BuffaloBites', '<p><ol><li onclick="handleItemClick()">Remove tofu from package and drain excess liquid. </li><li onclick="handleItemClick()">If pressing, place tofu block between two plates and place 2-3 cans on top. Place in refrigerator for 3-4 hours and drain additional water. You can skip this step if short on time. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1637\')" class="alarm-clock-icon"><div id="circular-timer-1637" class="circular-timer-container"><script>new CircularTimer("circular-timer-1637", 10800)</script><script>new CircularTimer("circular-timer-1637", 14400)</script></li><li onclick="handleItemClick()">Cube tofu into 1 inch cubes and place in a Tupperware container or baking dish large enough that cubes are close but not stacked. </li><li onclick="handleItemClick()">Prepare marinade by combining 1 tablespoon melted non-dairy butter with ½ cup hot sauce + ¼ cup water. Mix. Pour over tofu. Cover tofu. </li><li onclick="handleItemClick()">Place tofu in the refrigerator for 1-8 hours (or overnight). <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1638\')" class="alarm-clock-icon"><div id="circular-timer-1638" class="circular-timer-container"><script>new CircularTimer("circular-timer-1638", 3600)</script><script>new CircularTimer("circular-timer-1638", 28800)</script></li><li onclick="handleItemClick()">Preheat oven to 400 degrees Fahrenheit. </li><li onclick="handleItemClick()">Remove tofu from refrigerator and transfer to a bowl. Add cornstarch and gently toss until all tofu is lightly coated. </li><li onclick="handleItemClick()">Line a baking sheet with a Sil-pat or parchment paper. Transfer tofu to baking sheet, spaced evenly. </li><li onclick="handleItemClick()">Bake tofu for 30-45 minutes, until crispy on the outside, flipping halfway through. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1639\')" class="alarm-clock-icon"><div id="circular-timer-1639" class="circular-timer-container"><script>new CircularTimer("circular-timer-1639", 1800)</script><script>new CircularTimer("circular-timer-1639", 2700)</script></li><li onclick="handleItemClick()">Meanwhile, combine remaining ¼ cup hot sauce + 1 teaspoon melted non-dairy butter. Use to drench tofu immediately after removing from the oven. </li><li onclick="handleItemClick()">Serve immediately. </li></ol>' ] );
