ingredients_array.push( [ 'CostanzoRecipes_Sides_AsparagusRollswithHerbButterSauce', '<p><ul><li onclick="handleItemClick()">4 sheets of filo pastry </li><li onclick="handleItemClick()">¼ cup butter, melted </li><li onclick="handleItemClick()">16 young asparagus spears, trimmed </li></ul><h3 onclick="toggleSection(\'1229\')">Sauce<span id="heading-ellipses-1229"></h3><div id=\'heading-content-1229\'><p><ul><li onclick="handleItemClick()">2 shallots, finely chopped </li><li onclick="handleItemClick()">1 bay leaf </li><li onclick="handleItemClick()">⅔ cup dry white whine </li><li onclick="handleItemClick()">6 oz butter, softened </li><li onclick="handleItemClick()">1 tbsp chopped fresh herbs </li><li onclick="handleItemClick()">salt and freshly ground black pepper </li><li onclick="handleItemClick()">chopped chives, to garnish </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Sides_AsparagusRollswithHerbButterSauce', '<p><ol><li onclick="handleItemClick()">Preheat the oven to 400 degrees F. Cut the filo sheets in half. Brush a half sheet with melted butter. Fold one corner of the sheet down to the bottom edge to give a wedge shape. </li><li onclick="handleItemClick()">Lay 4 asparagus spears on top at the longest edge and roll up toward the shortest edge. Using the remaining filo and asparagus spears, make three more rolls in the same way. </li><li onclick="handleItemClick()">Lay the rolls on a greased baking sheet. Brush with the remaining melted butter. Bake in the oven for 8 minutes, until golden. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1632\')" class="alarm-clock-icon"><div id="circular-timer-1632" class="circular-timer-container"><script>new CircularTimer("circular-timer-1632", 480)</script></li><li onclick="handleItemClick()">Meanwhile, put the shallots, bay leaf and wine into a pan. Cover and cook over a high heat until the wine is reduced to 2-4 tbsp. </li><li onclick="handleItemClick()">Strain the wine mixture into a bowl. Whisk in the butter, a little at a time until the sauce is smooth and glossy. </li><li onclick="handleItemClick()">Stir in the herbs and add salt and pepper to taste. Return to the pan and keep the sauce warm. Serve the rolls on individual plates with a salad garnish if desired. Serve the sauce separately, sprinkled with a few chopped chives. </li></ol>' ] );
