ingredients_array.push( [ 'CostanzoRecipes_Entrees_ZucchiniandPepperEnchiladas', '<h3 onclick="toggleSection(\'1225\')">For the Enchiladas<span id="heading-ellipses-1225"></h3><div id=\'heading-content-1225\'><p><ul><li onclick="handleItemClick()">2 tablespoons olive oil </li><li onclick="handleItemClick()">2 medium sized cloves of garlic, crushed </li><li onclick="handleItemClick()">1 cup minced onion </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 cup minced green bell peppers </li><li onclick="handleItemClick()">2½ cups diced zucchini (about 2 smallish zukes) </li><li onclick="handleItemClick()">½ teaspoon <i class="conditional-space-after">each</i>: basil, cayenne, cumin, oregano and black pepper, to taste </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1226\')">For Mexican Hot Sauce<span id="heading-ellipses-1226"></h3><div id=\'heading-content-1226\'><p><ul><li onclick="handleItemClick()">1 cup chopped onion </li><li onclick="handleItemClick()">2 cloves crushed garlic </li><li onclick="handleItemClick()">3 cups chopped tomatoes </li><li onclick="handleItemClick()">1 cup water </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">1 teaspoon cumin </li><li onclick="handleItemClick()">¼ teaspoon ground corriander </li><li onclick="handleItemClick()">2 teaspoon dry red wine </li><li onclick="handleItemClick()">¼ - ½ teaspoon cayenne </li><li onclick="handleItemClick()">¼ teaspoon black pepper </li><li onclick="handleItemClick()">½ teaspoon chili powder </li><li onclick="handleItemClick()">2 tablespoons tomato paste </li><li onclick="handleItemClick()">2 tablespoons olive oil </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ZucchiniandPepperEnchiladas', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1227\')">For the Enchiladas<span id="heading-ellipses-1227"></h3><div id=\'heading-content-1227\'><p><ol><li onclick="handleItemClick()">In a large heavy skillet, sauté the garlic and onion in olive oil, with salt. </li><li onclick="handleItemClick()">When the onions are soft (after about 5 minutes), add the pepper, zucchini and herbs. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1630\')" class="alarm-clock-icon"><div id="circular-timer-1630" class="circular-timer-container"><script>new CircularTimer("circular-timer-1630", 300)</script></li><li onclick="handleItemClick()">Stir and continue to cook over medium heat another 5-8 minutes, or until the zucchini is just barely tender. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1631\')" class="alarm-clock-icon"><div id="circular-timer-1631" class="circular-timer-container"><script>new CircularTimer("circular-timer-1631", 300)</script><script>new CircularTimer("circular-timer-1631", 480)</script></li><li onclick="handleItemClick()">Remove from heat and stir in the cheese. </li><li onclick="handleItemClick()">Allow to cool at least to room temperature before filling enchiladas. </li></ol></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1228\')">For the Mexican Hot Sauce<span id="heading-ellipses-1228"></h3><div id=\'heading-content-1228\'><p><ol><li onclick="handleItemClick()">Sauté onions and garlic in olive oil with ½ teaspoon salt until onion is clear. </li><li onclick="handleItemClick()">Add spices. </li><li onclick="handleItemClick()">Transfer to saucepan and add tomatoes, water, tomato paste and wine. </li><li onclick="handleItemClick()">Add remaining salt. </li><li onclick="handleItemClick()">Cover and simmer ½ hour or longer (several hours is best). <div class="warning">   Hot spices tend to get hotter as they cook. It is recommended that you add the cayenne a little at a time so the degree of hotness is to your tolerance. </div><div class="note">   This sauce can be left in chunky form. Or you can purée all or part of it. </div></li></ol>' ] );
