ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetarianTofuCashewCoconutCurry', '<p><ul><li onclick="handleItemClick()">1 tablespoon virgin coconut oil </li><li onclick="handleItemClick()">3 cloves garlic, minced </li><li onclick="handleItemClick()">1 tablespoon freshly grated ginger </li><li onclick="handleItemClick()">1 jalapeño, diced </li><li onclick="handleItemClick()">1 medium sweet potato, diced into 1 inch cubes </li><li onclick="handleItemClick()">½ head of cauliflower, cut into small florets (about 2-3 cups) </li><li onclick="handleItemClick()">1 bell yellow or orange pepper, diced </li><li onclick="handleItemClick()">2 carrots, thinly diced or chopped </li><li onclick="handleItemClick()">2 tablespoons curry powder </li><li onclick="handleItemClick()">½ teaspoon turmeric </li><li onclick="handleItemClick()">½ teaspoon cumin </li><li onclick="handleItemClick()">⅛ teaspoon ground cinnamon </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 (15 oz) can lite coconut milk </li><li onclick="handleItemClick()">1/2 cup tomato sauce </li><li onclick="handleItemClick()">½ cup vegetarian broth </li><li onclick="handleItemClick()">¼ cup roasted cashews, ground </li><li onclick="handleItemClick()">1 package firm or extra firm Nasoya tofu, cubed </li><li onclick="handleItemClick()">To garnish: Cilantro and extra cashews </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetarianTofuCashewCoconutCurry', '<p><ol><li onclick="handleItemClick()">Add coconut oil to a large pot and place over medium high heat. Add in garlic, ginger, jalapeno, sweet potato, cauliflower, bell pepper, and carrots. Saute for 10 minutes, stirring frequently, until carrots start to soften. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1628\')" class="alarm-clock-icon"><div id="circular-timer-1628" class="circular-timer-container"><script>new CircularTimer("circular-timer-1628", 600)</script></li><li onclick="handleItemClick()">Next stir in curry powder, turmeric, cumin, cinnamon and salt.  </li><li onclick="handleItemClick()">Add in coconut milk, tomato sauce, vegetarian broth and ground cashews. Stir until smooth.  </li><li onclick="handleItemClick()">Gently add in tofu and stir. Simmer on low heat for 20 minutes or until sweet potatoes and carrots are fork tender. Serve immediately with cilantro. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1629\')" class="alarm-clock-icon"><div id="circular-timer-1629" class="circular-timer-container"><script>new CircularTimer("circular-timer-1629", 1200)</script></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_VegetarianTofuCashewCoconutCurry', '<p><ul><li onclick="handleItemClick()">From <a href="https://www.ambitiouskitchen.com/wprm_print/24300" target="_blank">https://www.ambitiouskitchen.com/wprm_print/24300</a> </li><li onclick="handleItemClick()">date created: [2021-03-13 Sat]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetarianLentilBurgerRecipe', '<p>These make great do-ahead meals, and you can store shaped, ready-to-cook patties in the refrigerator for a week\'s worth of work lunches. I use black lentils in this recipe, some of the other types of lentils are too mushy when cooked - so I\'d stick with the black lentils. Feel free to spice up the batter by experimenting with your favorite seasonings. <p><ul><li onclick="handleItemClick()">3 cups cooked black lentils </li><li onclick="handleItemClick()">4 large eggs </li><li onclick="handleItemClick()">½ teaspoon fine-grain sea salt </li><li onclick="handleItemClick()">1 onion, finely chopped </li><li onclick="handleItemClick()">1 cup toasted fine (whole-grain) bread crumbs </li><li onclick="handleItemClick()">1 tablespoon extra-virgin olive oil (or clarified butter) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetarianLentilBurgerRecipe', '<p><ol><li onclick="handleItemClick()">Combine the lentils, eggs, and salt in a food processor. Puree until the mixture is the consistency of a runny yet textured hummus - it\'s o.k. if many of the lentils remain whole (see photo). Pour into a mixing bowl and stir in the onion. Add the breadcrumbs, stir, and let sit for a couple of minutes so the crumbs can absorb some of the moisture. At this point, you should have a very moist mixture that you can easily form into twelve 1½-inch-thick patties. I err on the moist side here, because it makes for a nicely textured burger. You can always add more bread crumbs a bit at a time to firm up the dough if need be. Conversely, a bit of water or more egg can be used to moisten the batter. </li><li onclick="handleItemClick()">Heat the oil in a heavy skillet over medium low, add 4 patties, cover, and cook for 7 to 10 minutes, until the bottoms begin to brown. Turn up the heat if there is no browning after 10 minutes. Flip the patties and cook the second side for 7 minutes, or until golden. Remove from the skillet and cool on a wire rack while you cook the remaining patties. Carefully cut each patty in half, insert your favorite fillings, and enjoy immediately. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1627\')" class="alarm-clock-icon"><div id="circular-timer-1627" class="circular-timer-container"><script>new CircularTimer("circular-timer-1627", 420)</script><script>new CircularTimer("circular-timer-1627", 600)</script><script>new CircularTimer("circular-timer-1627", 600)</script><script>new CircularTimer("circular-timer-1627", 420)</script></li><li onclick="handleItemClick()">Makes 12 mini burgers. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_VegetarianLentilBurgerRecipe', '<p><ul><li onclick="handleItemClick()"><a href="http://www.101cookbooks.com/archives/print/vegetarian-lentil-burgers-recipe.html" target="_blank">http://www.101cookbooks.com/archives/print/vegetarian-lentil-burgers-recipe.html</a> </li><li onclick="handleItemClick()">date created: [2009-05-02 Sat]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetarianChili', '<p><ul><li onclick="handleItemClick()">1 tablespoon oil </li><li onclick="handleItemClick()">2 cloves garlic, minced </li><li onclick="handleItemClick()">2 28-ounce cans tomatoes, undrained </li><li onclick="handleItemClick()">2 medium onions, sliced </li><li onclick="handleItemClick()">1 large green pepper, cut into 1" pieces </li><li onclick="handleItemClick()">2 cups chopped celery </li><li onclick="handleItemClick()">3 15½-ounce cans of beans (some mixture of garbanzo, red kidney and/or black beans), drained and rinsed </li><li onclick="handleItemClick()">1 4-ounce can chopped green chili peppers </li><li onclick="handleItemClick()">¼ cup vinegar </li><li onclick="handleItemClick()">1 tablespoon chili powder </li><li onclick="handleItemClick()">2 teaspoons bottled hot pepper sauce (Franks, anyone?) </li><li onclick="handleItemClick()">1½ teaspoons dried basil, crushed </li><li onclick="handleItemClick()">1½ teaspoons dried oregano, crushed </li><li onclick="handleItemClick()">1½ teaspoon ground cumin </li><li onclick="handleItemClick()">½ teaspoon ground allspice </li><li onclick="handleItemClick()">½ teaspoon ground black pepper </li><li onclick="handleItemClick()">1 bay leaf </li><li onclick="handleItemClick()">1 12-ounce can beer </li><li onclick="handleItemClick()">1 cup peanuts or cashews (optional) </li><li onclick="handleItemClick()">1 cup shredded Cheddar cheese </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetarianChili', '<p><ol><li onclick="handleItemClick()">Preheat oil in cooker at 375 degrees. </li><li onclick="handleItemClick()">Add onions and green pepper and sauté until onions have begun to brown. </li><li onclick="handleItemClick()">Add garlic and sauté for 30 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1625\')" class="alarm-clock-icon"><div id="circular-timer-1625" class="circular-timer-container"><script>new CircularTimer("circular-timer-1625", 30)</script></li><li onclick="handleItemClick()">Add tomatoes, celery, beans, chili peppers, vinegar and seasonings. </li><li onclick="handleItemClick()">Cover, bring to a boil then reduce to a simmer for 1½ hours. </li><li onclick="handleItemClick()">Add beer and simmer, uncovered for 30 minutes or until desired consistency. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1626\')" class="alarm-clock-icon"><div id="circular-timer-1626" class="circular-timer-container"><script>new CircularTimer("circular-timer-1626", 1800)</script></li><li onclick="handleItemClick()">Remove bay leaf. </li><li onclick="handleItemClick()">Add nuts, if desired. </li><li onclick="handleItemClick()">Garnish with cheese </li></ol>' ] );
