ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetableRiggies', '<p>Prep time: 20 min Cook time: 45 min<br>Ready in: 1 hour 5 min<img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1622\')" class="alarm-clock-icon"><div id="circular-timer-1622" class="circular-timer-container"><script>new CircularTimer("circular-timer-1622", 3600)</script><br>Yields: Serves 6 to 10 <p><ul><li onclick="handleItemClick()">12 medium-sized mushrooms, sliced </li><li onclick="handleItemClick()">regular sized bag green onions, chopped </li><li onclick="handleItemClick()">½ clove garlic, minced or pressed </li><li onclick="handleItemClick()">¾ stick butter, melted in pan </li><li onclick="handleItemClick()">1 to 2 lbs various peppers of your choice, cut however your heart desires </li><li onclick="handleItemClick()">1 to 2 lbs High-quality rigatoni pasta </li><li onclick="handleItemClick()">2 large cans crushed tomatoes </li><li onclick="handleItemClick()">½ to 1 white onion, preferably in rings </li><li onclick="handleItemClick()">a handful Italian seasonings, pepper and salt, tossed in </li><li onclick="handleItemClick()">1 quart heavy cream </li><li onclick="handleItemClick()">1 tablespoon extra virgin olive oil </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetableRiggies', '<p><ol><li onclick="handleItemClick()">Chop up all your veggies, place the mushrooms and scallions in one bowl, and have all your peppers in another bowl. </li><li onclick="handleItemClick()">Put the crushed tomatoes in a medium sauce pan. Put on a low heat, adding white onion and all the seasonings. Base amounts on your liking. Periodically stir. </li><li onclick="handleItemClick()">Heat butter and garlic in a large high sided frying pan on a medium heat. Once garlic smell comes off the pan, immediately add mushrooms and scallions. Saute until lightly browned, roughly 10 minutes. Get your pasta water started on the heat. Add olive oil to water. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1623\')" class="alarm-clock-icon"><div id="circular-timer-1623" class="circular-timer-container"><script>new CircularTimer("circular-timer-1623", 600)</script></li><li onclick="handleItemClick()">Add peppers. Keep heat close to high. Let cook for about 10 more minutes. Your pasta is done. </li><li onclick="handleItemClick()">Add heavy cream in with peppers. Let cook on medium heat for about 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1624\')" class="alarm-clock-icon"><div id="circular-timer-1624" class="circular-timer-container"><script>new CircularTimer("circular-timer-1624", 900)</script></li><li onclick="handleItemClick()">Combine everything... <i class="conditional-space-after">Boom!</i> </li></ol>' ] );
