ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetablePotPie', '<p>Prep Time: 1 hr  Total Time: 1 hr 45 mins Servings: 6-8 <p><ul><li onclick="handleItemClick()">1 cup potato, diced </li><li onclick="handleItemClick()">1 cup onion, diced </li><li onclick="handleItemClick()">1 cup celery, diced </li><li onclick="handleItemClick()">1 cup carrots, diced </li><li onclick="handleItemClick()">⅓ cup melted butter </li><li onclick="handleItemClick()">½ cup all-purpose flour </li><li onclick="handleItemClick()">2 cups vegetable broth </li><li onclick="handleItemClick()">1 cup half-and-half </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">¼ teaspoon pepper </li><li onclick="handleItemClick()">½ teaspoon of rosemary (optional) </li><li onclick="handleItemClick()">½ teaspoon of thyme (optional) </li><li onclick="handleItemClick()">1 cup peas </li><li onclick="handleItemClick()">1 cup TVP or other mock meat (optional) </li><li onclick="handleItemClick()">1 pie crust (either store bought or your own recipe) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetablePotPie', '<p><ol><li onclick="handleItemClick()">Preheat oven to 400 degrees F. </li><li onclick="handleItemClick()">Saute onion, celery, carrots and potatoes in butter for 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1619\')" class="alarm-clock-icon"><div id="circular-timer-1619" class="circular-timer-container"><script>new CircularTimer("circular-timer-1619", 600)</script></li><li onclick="handleItemClick()">Add flour to sauteed mixture, stirring well, cook one minute stirring constantly. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1620\')" class="alarm-clock-icon"><div id="circular-timer-1620" class="circular-timer-container"><script>new CircularTimer("circular-timer-1620", 60)</script></li><li onclick="handleItemClick()">Combine broth and half and half; gradually stir into vegetable mixture. </li><li onclick="handleItemClick()">Cook over medium heat stirring constantly until thickened and bubbly. </li><li onclick="handleItemClick()">Stir in salt and pepper; add peas and TVP or other mock meat (if using) and stir well. </li><li onclick="handleItemClick()">Pour into shallow 2 quart casserole dish and top with pie shell. </li><li onclick="handleItemClick()">Cut slits to allow steam to escape. </li><li onclick="handleItemClick()">Bake for 40-50 minutes or until pastry is golden brown and filling is bubbly and cooked through. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1621\')" class="alarm-clock-icon"><div id="circular-timer-1621" class="circular-timer-container"><script>new CircularTimer("circular-timer-1621", 2400)</script><script>new CircularTimer("circular-timer-1621", 3000)</script></li></ol>' ] );
