ingredients_array.push( [ 'CostanzoRecipes_Entrees_VegetableEnchiladas', '<h3 onclick="toggleSection(\'1221\')">For the Enchiladas<span id="heading-ellipses-1221"></h3><div id=\'heading-content-1221\'><p><ul><li onclick="handleItemClick()">3 tbsp. vegetable oil </li><li onclick="handleItemClick()">1 onion, minced </li><li onclick="handleItemClick()">1 clove garlic, minced or pressed </li><li onclick="handleItemClick()">1 green pepper, chopped fine </li><li onclick="handleItemClick()">3 stalks celery, chopped fine </li><li onclick="handleItemClick()">1 c. chopped parsley </li><li onclick="handleItemClick()">1 c. coarsely grated zucchini </li><li onclick="handleItemClick()">2 c. green beans, chopped sm. </li><li onclick="handleItemClick()">1 tsp. salt </li><li onclick="handleItemClick()">Dash each of chili, cumin powder, corn flour </li><li onclick="handleItemClick()">2 c. grated cheddar or Jack cheese </li><li onclick="handleItemClick()">3½ c. Red Sauce </li><li onclick="handleItemClick()">12 corn or whole wheat flour tortillas </li><li onclick="handleItemClick()">½ c. chopped black olives </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1222\')">For the Red Sauce<span id="heading-ellipses-1222"></h3><div id=\'heading-content-1222\'><p><ul><li onclick="handleItemClick()">½ large onion, chopped </li><li onclick="handleItemClick()">2 tbsp. oil </li><li onclick="handleItemClick()">1 bay leaf </li><li onclick="handleItemClick()">1 tsp. cumin </li><li onclick="handleItemClick()">3 c. tomatoes </li><li onclick="handleItemClick()">1 tsp. salt </li><li onclick="handleItemClick()">1 clove garlic, minced </li><li onclick="handleItemClick()">2 tbsp. chopped green pepper </li><li onclick="handleItemClick()">1 tsp. oregano </li><li onclick="handleItemClick()">½ tsp. chili powder </li><li onclick="handleItemClick()">1 (6 oz.) can tomato paste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_VegetableEnchiladas', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1223\')">For the Enchiladas<span id="heading-ellipses-1223"></h3><div id=\'heading-content-1223\'><p><ol><li onclick="handleItemClick()">Saute onion, garlic, pepper and celery in oil. Add parsley, zucchini and green beans with salt, cumin, and chili powder and cook, covered, until tender. </li><li onclick="handleItemClick()">If vegetables are very wet, thicken with corn flour. Add 1 cup of the cheese and ¾ cup of the red sauce. Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">Fill each tortilla with ⅓ cup of vegetable mixture. Roll them up and place in a baking dish, seam-side down, close together and cover with remaining sauce. Top with remaining cheese and olives and heat until sauce bubbles. </li></ol></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1224\')">For the Red Sauce<span id="heading-ellipses-1224"></h3><div id=\'heading-content-1224\'><p><ol><li onclick="handleItemClick()">Saute onion and garlic in oil. </li><li onclick="handleItemClick()">Add pepper, bay leaf, oregano, cumin, chili powder, tomatoes, tomato paste and salt. </li><li onclick="handleItemClick()">Simmer for half and hour and remove bay leaf. </li></ol>' ] );
