ingredients_array.push( [ 'CostanzoRecipes_Entrees_Tofubanhmisandwich', '<h3 onclick="toggleSection(\'1218\')">Tofu<span id="heading-ellipses-1218"></h3><div id=\'heading-content-1218\'><p><ul><li onclick="handleItemClick()">¼ cup tamari </li><li onclick="handleItemClick()">2 tbsp lime juice </li><li onclick="handleItemClick()">2 Tbsp (coconut) sugar </li><li onclick="handleItemClick()">2 tsp sesame oil </li><li onclick="handleItemClick()">1 garlic clove, chopped </li><li onclick="handleItemClick()">1 1-inch cube ginger, chopped </li><li onclick="handleItemClick()">1 lemongrass stalk, roughly chopped  </li><li onclick="handleItemClick()">½ tsp crushed red chili flakes </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1219\')">Pickled veggies<span id="heading-ellipses-1219"></h3><div id=\'heading-content-1219\'><p><ul><li onclick="handleItemClick()">½ cup white vinegar </li><li onclick="handleItemClick()">½ cup water </li><li onclick="handleItemClick()">3 tbsp sugar </li><li onclick="handleItemClick()">1 tsp salt </li><li onclick="handleItemClick()">½ cup carrots, cut into sticks or peeled </li><li onclick="handleItemClick()">½ cup daikon, cut into sticks or peeled </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1220\')">Other<span id="heading-ellipses-1220"></h3><div id=\'heading-content-1220\'><p><ul><li onclick="handleItemClick()">1 baguette </li><li onclick="handleItemClick()">¼ cucumber, sliced </li><li onclick="handleItemClick()">green onions </li><li onclick="handleItemClick()">(vegan) mayonnaise  </li><li onclick="handleItemClick()">hoisin sauce </li><li onclick="handleItemClick()">cilantro (optional) </li><li onclick="handleItemClick()">sriracha (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_Tofubanhmisandwich', '<p><ol><li onclick="handleItemClick()"><i class="conditional-space-after">Make the tofu</i>: Mix the marinade together and place the tofu in the marinade. Let sit overnight or at least 1 hour before baking. Bake tofu at 420° for 20-25 minutes until golden brown. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1614\')" class="alarm-clock-icon"><div id="circular-timer-1614" class="circular-timer-container"><script>new CircularTimer("circular-timer-1614", 3600)</script><script>new CircularTimer("circular-timer-1614", 1200)</script><script>new CircularTimer("circular-timer-1614", 1500)</script></li><li onclick="handleItemClick()"><i class="conditional-space-after">Make the pickled veggies</i>: Mix the vinegar, water, sugar and salt together in a bowl. Cut the carrots and daikons into thin sticks and place in the pickling mixture overnight or for at least an hour. </li><li onclick="handleItemClick()"><i class="conditional-space-after">Build your banh mi</i>: Cut a piece of the baguette and spread some vegan mayonnaise and hoisin sauce onto the bread. Fill with the marinated tofu, pickled veggies, sliced cucumber, green onions and cilantro. Optionally add some sriracha for spice! </li><li onclick="handleItemClick()">If you like your bread warm, gently heat the banh mi in a panini press for 1-2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1615\')" class="alarm-clock-icon"><div id="circular-timer-1615" class="circular-timer-container"><script>new CircularTimer("circular-timer-1615", 60)</script><script>new CircularTimer("circular-timer-1615", 120)</script><div class="tip">   Try serving in <a href="https://john-costanzo.github.io/recipes-rb.html#CostanzoRecipes_Bread_VeganBurgerBuns" target="_blank">Vegan Burger Buns</a>. </div></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_Tofubanhmisandwich', '<p>Favorite <p><ul><li onclick="handleItemClick()">From <a href="https://choosingchia.com/tofu-banh-mi-sandwich/" target="_blank">https://choosingchia.com/tofu-banh-mi-sandwich/</a> </li><li onclick="handleItemClick()">date created: [2022-01-20 Thu]  </li><li onclick="handleItemClick()">[2022-01-20 Thu] Made for T and me. I marinated the tofu and veggies for about 3 hours. I also cooked the tofu on convection for 25 minutes. I left the sandwiches in the panini maker for about 10 minutes and they got warm throughout. We both enjoyed these! <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1616\')" class="alarm-clock-icon"><div id="circular-timer-1616" class="circular-timer-container"><script>new CircularTimer("circular-timer-1616", 10800)</script><script>new CircularTimer("circular-timer-1616", 1500)</script><script>new CircularTimer("circular-timer-1616", 600)</script></li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_ThaiRedCurrySummerSquash', '<p>by mlsawatzky of Sandbrook Meadow Farm <p><ul><li onclick="handleItemClick()">3 medium summer squash cubed </li><li onclick="handleItemClick()">1 sweet onion, sliced </li><li onclick="handleItemClick()">2 cloves of garlic sliced </li><li onclick="handleItemClick()">2 TBLSP olive oil </li><li onclick="handleItemClick()">1 - 2 TBLSP red curry paste (MASRE brand good) </li><li onclick="handleItemClick()">1 - 2 cans coconut milk (lite is good) </li><li onclick="handleItemClick()">1¼ TBLSP fish sauce </li><li onclick="handleItemClick()">1 TBLSP brown sugar </li><li onclick="handleItemClick()">Salt </li><li onclick="handleItemClick()">Sweet (Thai) Basil </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ThaiRedCurrySummerSquash', '<p><ol><li onclick="handleItemClick()">Saute curry paste in olive oil 1-2 minutes to bring out flavor.  Add 1 cup coconut milk  & simmer 4 minutes.  Add onion, garlic and squash.  Cook until beginning to get tender.  Add 1½ cup coconut milk or water and fish sauce & sugar.  Add salt to taste & remove from heat and add sweet basil <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1613\')" class="alarm-clock-icon"><div id="circular-timer-1613" class="circular-timer-container"><script>new CircularTimer("circular-timer-1613", 60)</script><script>new CircularTimer("circular-timer-1613", 120)</script><script>new CircularTimer("circular-timer-1613", 240)</script></li><li onclick="handleItemClick()">May use other vegetables as well </li><li onclick="handleItemClick()">From Mary Laura @ Sandbrook 11-July-2012 </li></ol>' ] );
