ingredients_array.push( [ 'CostanzoRecipes_Entrees_SwissMushroomandSpinachFrittata', '<p><ul><li onclick="handleItemClick()">1-2 tablespoons extra virgin olive oil </li><li onclick="handleItemClick()">8 ounces button mushrooms, sliced (other mushrooms such as crimini may be substituted) </li><li onclick="handleItemClick()">1 garlic clove, minced </li><li onclick="handleItemClick()">5 cups baby spinach </li><li onclick="handleItemClick()">10 large eggs, separated </li><li onclick="handleItemClick()">1 cup half-and-half or whole milk </li><li onclick="handleItemClick()">1½ cup + ¼ cup shredded Emmi USA Kaltbach Emmentaler AOP cheese, divided </li><li onclick="handleItemClick()">1 teaspoon kosher salt </li><li onclick="handleItemClick()">½ teaspoon ground black pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SwissMushroomandSpinachFrittata', '<p><ol><li onclick="handleItemClick()">Place an oven rack on the center or upper-middle shelf, then preheat oven to 400 degrees F. </li><li onclick="handleItemClick()">Heat the olive oil in a 12-inch oven-safe nonstick or cast iron skillet over medium-high heat. Use just enough oil to coat bottom of the pan; you might not need the entire amount. Add the mushrooms and cook until soft and fragrant, 3-4 minutes. Adjust the heat to medium-low and add the garlic, stirring for an additional 30-60 seconds. Add the spinach in batches, stirring as it wilts. Once all of the spinach has been wilted, remove the pan from the heat. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1611\')" class="alarm-clock-icon"><div id="circular-timer-1611" class="circular-timer-container"><script>new CircularTimer("circular-timer-1611", 180)</script><script>new CircularTimer("circular-timer-1611", 240)</script><script>new CircularTimer("circular-timer-1611", 30)</script><script>new CircularTimer("circular-timer-1611", 60)</script></li><li onclick="handleItemClick()">In a large bowl, beat the egg whites to a stiff peak using an electric hand mixer or stand mixer with the whisk attachment. In a separate large bowl, whisk together the yolks, then add the half-and-half, 1½ cups cheese, salt and pepper. </li><li onclick="handleItemClick()">Gently fold the whites into the yolks using a spatula. Once evenly combined, pour the egg mixture over the vegetables. Sprinkle the remaining cheese evenly over the top. </li><li onclick="handleItemClick()">Bake for 10-13 minutes, or until the eggs have fluffed up and set with a golden top. Cut the frittata into wedges and serve. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1612\')" class="alarm-clock-icon"><div id="circular-timer-1612" class="circular-timer-container"><script>new CircularTimer("circular-timer-1612", 600)</script><script>new CircularTimer("circular-timer-1612", 780)</script></li><li onclick="handleItemClick()">The frittata is best served fresh, but leftovers can be refrigerated in an airtight container for up to 5 days. Microwave to reheat for a quick breakfast, lunch, or dinner. </li></ol>' ] );
