ingredients_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoandBlackBeanChili', '<p><ul><li onclick="handleItemClick()">1 tablespoon plus 2 teaspoons extra-virgin olive oil </li><li onclick="handleItemClick()">1 medium-large sweet potato, peeled and diced </li><li onclick="handleItemClick()">1 large onion, diced </li><li onclick="handleItemClick()">4 cloves garlic, minced </li><li onclick="handleItemClick()">2 tablespoons chili powder </li><li onclick="handleItemClick()">4 teaspoons ground cumin </li><li onclick="handleItemClick()">1/2 teaspoon ground chipotle chile (see Note) </li><li onclick="handleItemClick()">¼ teaspoon salt </li><li onclick="handleItemClick()">2 ½ cups water </li><li onclick="handleItemClick()">2 15-ounce cans black beans, rinsed </li><li onclick="handleItemClick()">1 14-ounce can diced tomatoes </li><li onclick="handleItemClick()">4 teaspoons lime juice </li><li onclick="handleItemClick()">½ cup chopped fresh cilantro </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoandBlackBeanChili', '<p><ol><li onclick="handleItemClick()">Heat oil in a Dutch oven over medium-high heat. </li><li onclick="handleItemClick()">Add sweet potato and onion and cook, stirring often, until the onion is beginning to soften, about 4 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1607\')" class="alarm-clock-icon"><div id="circular-timer-1607" class="circular-timer-container"><script>new CircularTimer("circular-timer-1607", 240)</script></li><li onclick="handleItemClick()">Add garlic, chili powder, cumin, chipotle and salt and cook, stirring constantly, for 30 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1608\')" class="alarm-clock-icon"><div id="circular-timer-1608" class="circular-timer-container"><script>new CircularTimer("circular-timer-1608", 30)</script></li><li onclick="handleItemClick()">Add water and bring to a simmer. </li><li onclick="handleItemClick()">Cover, reduce heat to maintain a gentle simmer and cook until the sweet potato is tender, 10 to 12 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1609\')" class="alarm-clock-icon"><div id="circular-timer-1609" class="circular-timer-container"><script>new CircularTimer("circular-timer-1609", 600)</script><script>new CircularTimer("circular-timer-1609", 720)</script></li><li onclick="handleItemClick()">Add beans, tomatoes and lime juice; increase heat to high and return to a simmer, stirring often. </li><li onclick="handleItemClick()">Reduce heat and simmer until slightly reduced, about 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1610\')" class="alarm-clock-icon"><div id="circular-timer-1610" class="circular-timer-container"><script>new CircularTimer("circular-timer-1610", 300)</script></li><li onclick="handleItemClick()">Remove from heat and stir in cilantro. </li></ol>' ] );
